using Gtk;
using InterfaceGtk4;

using GeneratedCode.Довідники;
using GeneratedCode.Документи;
using GeneratedCode.Перелічення;

namespace StorageAndTrade;

class Організації_Елемент : DirectoryFormElement
{
    public Організації_Objest Елемент { get; init; } = new Організації_Objest();

    #region Fields
    Entry Код = new() { WidthRequest = 100 };
                    Entry Назва = new() { WidthRequest = 500 };
                    Entry НазваПовна = new() { WidthRequest = 500 };
                    Entry НазваСкорочена = new() { WidthRequest = 500 };
                    DateTimeControl ДатаРеєстрації = new(){ OnlyDate = true };
            Entry КраїнаРеєстрації = new() { WidthRequest = 500 };
                    Entry СвідоцтвоСеріяНомер = new() { WidthRequest = 200 };
                    Entry СвідоцтвоДатаВидачі = new() { WidthRequest = 200 };
                    Організації_PointerControl Холдинг = new() { Caption = "Холдинг", WidthPresentation = 500 };

    #endregion

    #region TabularParts

        // Таблична частина "Контакти"
        Організації_ТабличнаЧастина_Контакти Контакти = new() { WidthRequest = 500, HeightRequest = 300 };

    #endregion

    public Організації_Елемент() : base(Program.BasicForm?.NotebookFunc)
    { 
        Element = Елемент;

    }

    protected override void CreateStart(Box vBox)
    {

            // Код
            CreateField(vBox, "Код:", Код);

            // Назва
            CreateField(vBox, "Назва:", Назва);

            // НазваПовна
            CreateField(vBox, "Назва повна:", НазваПовна);

            // НазваСкорочена
            CreateField(vBox, "Назва скорочена:", НазваСкорочена);

            // ДатаРеєстрації
            CreateField(vBox, "Дата реєстрації:", ДатаРеєстрації);

            // КраїнаРеєстрації
            CreateField(vBox, "Країна реєстрації:", КраїнаРеєстрації);

            // СвідоцтвоСеріяНомер
            CreateField(vBox, "Свідоцтво серія номер:", СвідоцтвоСеріяНомер);

            // СвідоцтвоДатаВидачі
            CreateField(vBox, "Свідоцтво дата видачі:", СвідоцтвоДатаВидачі);

            // Холдинг
            CreateField(vBox, null, Холдинг);

    }

    protected override void CreateEnd(Box vBox)
    {

            // Таблична частина "Контакти" 
            CreateTablePart(vBox, "Контакти:", Контакти);

    }

    #region Присвоєння / зчитування значень

    public override async ValueTask AssignValue()
    {
        Код.SetText(Елемент.Код);
                        Назва.SetText(Елемент.Назва);
                        НазваПовна.SetText(Елемент.НазваПовна);
                        НазваСкорочена.SetText(Елемент.НазваСкорочена);
                        ДатаРеєстрації.Value = Елемент.ДатаРеєстрації;
                КраїнаРеєстрації.SetText(Елемент.КраїнаРеєстрації);
                        СвідоцтвоСеріяНомер.SetText(Елемент.СвідоцтвоСеріяНомер);
                        СвідоцтвоДатаВидачі.SetText(Елемент.СвідоцтвоДатаВидачі);
                        Холдинг.Pointer = Елемент.Холдинг;

            // Таблична частина "Контакти"
            Контакти.ЕлементВласник = Елемент;
            await Контакти.LoadRecords();

    }

    protected override void GetValue()
    {
        Елемент.Код = Код.GetText();
                        Елемент.Назва = Назва.GetText();
                        Елемент.НазваПовна = НазваПовна.GetText();
                        Елемент.НазваСкорочена = НазваСкорочена.GetText();
                        Елемент.ДатаРеєстрації = ДатаРеєстрації.Value;
                Елемент.КраїнаРеєстрації = КраїнаРеєстрації.GetText();
                        Елемент.СвідоцтвоСеріяНомер = СвідоцтвоСеріяНомер.GetText();
                        Елемент.СвідоцтвоДатаВидачі = СвідоцтвоДатаВидачі.GetText();
                        Елемент.Холдинг = Холдинг.Pointer;

    }

    #endregion

    protected override async ValueTask<bool> Save()
    {
        bool isSaved = false;
        try
        {
            if (await Елемент.Save())
            {
                await Контакти.SaveRecords(); // Таблична частина "Контакти"

                isSaved = true;
            }
        }
        catch (Exception ex)
        {
            //ФункціїДляПовідомлень.ДодатиПовідомлення(Елемент.GetBasis(), Caption, ex);
        }
        return isSaved;
    }
}

/*     
        Організації_PointerControl.cs
        PointerControl
*/

using Gtk;
using InterfaceGtk4;
using GeneratedCode.Довідники;

namespace StorageAndTrade;

public class Організації_PointerControl : PointerControl
{
    event EventHandler<Організації_Pointer> PointerChanged;

    public Організації_PointerControl()
    {
        pointer = new Організації_Pointer();
        WidthPresentation = 300;
        Caption = $"{Організації_Const.FULLNAME}:";
        PointerChanged += async (_, pointer) => Presentation = pointer != null ? await pointer.GetPresentation() : "";
    }

    Організації_Pointer pointer;
    public Організації_Pointer Pointer
    {
        get
        {
            return pointer;
        }
        set
        {
            pointer = value;
            PointerChanged?.Invoke(null, pointer);
        }
    }



    protected override async void OpenSelect(Button button, EventArgs args)
    {
        Popover popover = Popover.New();
        popover.SetParent(button);
        popover.WidthRequest = 800;
        popover.HeightRequest = 400;
        BeforeClickOpenFunc?.Invoke();
        Організації_ШвидкийВибір page = new()
        {
            PopoverParent = popover,
            DirectoryPointerItem = Pointer.UnigueID,
            CallBack_OnSelectPointer = selectPointer =>
            {
                Pointer = new Організації_Pointer(selectPointer);
                AfterSelectFunc?.Invoke();
            }
        };

        popover.SetChild(page);
        popover.Show();

        await page.SetValue();
    }

    protected override void OnClear(Button button, EventArgs args)
    {
        Pointer = new Організації_Pointer();
        AfterSelectFunc?.Invoke();
        AfterClearFunc?.Invoke();
    }
}

/*
        Організації_Функції.cs
        Функції
*/

using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Довідники;

namespace StorageAndTrade;

static class Організації_Функції
{
    public static List<Where> Відбори(string searchText)
    {
        return
        [

            //Назва
            new Where(Організації_Const.Назва, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //Код
            new Where(Comparison.OR, Організації_Const.Код, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //НазваСкорочена
            new Where(Comparison.OR, Організації_Const.НазваСкорочена, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //СвідоцтвоСеріяНомер
            new Where(Comparison.OR, Організації_Const.СвідоцтвоСеріяНомер, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //СвідоцтвоДатаВидачі
            new Where(Comparison.OR, Організації_Const.СвідоцтвоДатаВидачі, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //КлючовіСловаДляПошуку
            new Where(Comparison.OR, Організації_Const.КлючовіСловаДляПошуку, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

        ];
    }

    public static async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null, 
        Action<UnigueID?>? сallBack_LoadRecords = null, 
        Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        Організації_Елемент page = new()
        {
            CallBack_LoadRecords = сallBack_LoadRecords,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        if (IsNew)
        {
            await page.Елемент.New();

        }
        else if (unigueID == null || !await page.Елемент.Read(unigueID))
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return;
        }

        Program.BasicForm?.NotebookFunc.CreatePage(page.Caption, page);
        await page.SetValue();
    }

    public static async ValueTask OpenPageList(UnigueID? unigueID = null, Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        Організації_Список page = new()
        {
            DirectoryPointerItem = unigueID,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        Program.BasicForm?.NotebookFunc.CreatePage(Організації_Const.FULLNAME, page);
        await page.SetValue();
    }

    public static async ValueTask SetDeletionLabel(UnigueID unigueID)
    {
        Організації_Pointer Вказівник = new(unigueID);
        bool? label = await Вказівник.GetDeletionLabel();
        if (label.HasValue) await Вказівник.SetDeletionLabel(!label.Value);
    }

    public static async ValueTask<UnigueID?> Copy(UnigueID unigueID)
    {
        Організації_Objest Обєкт = new();
        if (await Обєкт.Read(unigueID))
        {
            Організації_Objest Новий = await Обєкт.Copy(true);
            await Новий.Save();

                await Новий.Контакти_TablePart.Save(false); // Таблична частина "Контакти"

            return Новий.UnigueID;
        }
        else
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return null;
        }
    }
}

/*     
        Організації_PointerTablePartCell.cs
        PointerTablePartCell
*/
using Gtk;
using InterfaceGtk4;
using GeneratedCode.Довідники;
using AccountingSoftware;

namespace StorageAndTrade;

public class Організації_PointerTablePartCell : PointerTablePartCell
{
    Організації_Pointer pointer = new();
    public Організації_Pointer Pointer
    {
        get => pointer;
        set
        {
            pointer = value;
            Presentation = pointer.Name;
        }
    }

    public async ValueTask GetPresentation() => Presentation = pointer != null ? await pointer.GetPresentation() : "";

    async ValueTask PointerChange(UnigueID? p)
    {
        Pointer = new Організації_Pointer(p ?? new UnigueID());
        await GetPresentation();
        OnSelect?.Invoke();
    }



    protected override async void Select(Button button, EventArgs args)
    {
        Popover popover = Popover.New();
        popover.SetParent(button);
        popover.WidthRequest = 800;
        popover.HeightRequest = 400;

        Організації_ШвидкийВибір page = new()
        {
            PopoverParent = popover,
            DirectoryPointerItem = pointer.UnigueID,
            CallBack_OnSelectPointer = async p => await PointerChange(p)
        };


        popover.SetChild(page);
        popover.Show();

        await page.SetValue();
    }

    protected override async void Clear()
    {
        await PointerChange(null);
    }
}

/*
        Номенклатура_ТабличнаЧастина_Файли.cs
        Таблична Частина
*/

using Gtk;
using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Довідники;
using GeneratedCode.Документи;
using GeneratedCode.Перелічення;

namespace StorageAndTrade;

class Номенклатура_ТабличнаЧастина_Файли : DirectoryFormTablePart
{

    public Номенклатура_Objest? ЕлементВласник { get; set; }


    #region Data

    class ItemRow : RowTablePart
    {

        //
        // НомерРядка
        //
        public int НомерРядка
        {
            get => НомерРядка_;
            set
            {
                if (!НомерРядка_.Equals(value))
                {
                    НомерРядка_ = value;
                    Сhanged_НомерРядка?.Invoke();
                }
            }
        }
        int НомерРядка_ = 0;
        public Action? Сhanged_НомерРядка;


        //
        // Основний
        //
        public bool Основний
        {
            get => Основний_;
            set
            {
                if (!Основний_.Equals(value))
                {
                    Основний_ = value;
                    Сhanged_Основний?.Invoke();
                }
            }
        }
        bool Основний_ = false;
        public Action? Сhanged_Основний;


        //
        // Файл
        //
        public Файли_Pointer Файл
        {
            get => Файл_;
            set
            {
                if (!Файл_.Equals(value))
                {
                    Файл_ = value;
                    Сhanged_Файл?.Invoke();
                }
            }
        }
        Файли_Pointer Файл_ = new();
        public Action? Сhanged_Файл;



        /*
        Функції
        */

        public override ItemRow Copy()
        {
            return new()
            {
                НомерРядка = НомерРядка,
                Основний = Основний,
                Файл = Файл.Copy(),

            };
        }
    }

    #endregion

    protected override Gio.ListStore Store { get; } = Gio.ListStore.New(ItemRow.GetGType());

    public Номенклатура_ТабличнаЧастина_Файли() : base(Program.BasicForm?.NotebookFunc)
    {
        MultiSelection model = MultiSelection.New(Store);
        model.OnSelectionChanged += GridOnSelectionChanged;

        Grid.Model = model;
    }

    protected override void Columns()
    {

        //НомерРядка
        {
            SignalListItemFactory factory = SignalListItemFactory.New();
            factory.OnSetup += (_, args) =>
            {
                ListItem listItem = (ListItem)args.Object;
                var cell = LabelTablePartCell.New(null);

                cell.Halign = Align.End;

                listItem.Child = cell;
            };
            factory.OnBind += (_, args) =>
            {
                ListItem listItem = (ListItem)args.Object;
                var cell = (LabelTablePartCell?)listItem.Child;
                ItemRow? row = (ItemRow?)listItem.Item;
                if (cell != null && row != null)
                {

                    (row.Сhanged_НомерРядка = () => cell.SetText(row.НомерРядка)).Invoke();

                }
            };
            ColumnViewColumn column = ColumnViewColumn.New("№", factory);
            column.Resizable = true;

            Grid.AppendColumn(column);
        }

        //Основний
        {
            SignalListItemFactory factory = SignalListItemFactory.New();
            factory.OnSetup += (_, args) =>
            {
                ListItem listItem = (ListItem)args.Object;
                var cell = new CheckTablePartCell();

                cell.Halign = Align.Center;

                listItem.Child = cell;
            };
            factory.OnBind += (_, args) =>
            {
                ListItem listItem = (ListItem)args.Object;
                var cell = (CheckTablePartCell?)listItem.Child;
                ItemRow? row = (ItemRow?)listItem.Item;
                if (cell != null && row != null)
                {

                    cell.OnСhanged = () => row.Основний = cell.Check.Active;
                    (row.Сhanged_Основний = () => cell.Value = row.Основний).Invoke();

                }
            };
            ColumnViewColumn column = ColumnViewColumn.New("Основний", factory);
            column.Resizable = true;

            Grid.AppendColumn(column);
        }

        //Файл
        {
            SignalListItemFactory factory = SignalListItemFactory.New();
            factory.OnSetup += (_, args) =>
            {
                ListItem listItem = (ListItem)args.Object;
                var cell = new Файли_PointerTablePartCell();

                listItem.Child = cell;
            };
            factory.OnBind += (_, args) =>
            {
                ListItem listItem = (ListItem)args.Object;
                var cell = (Файли_PointerTablePartCell?)listItem.Child;
                ItemRow? row = (ItemRow?)listItem.Item;
                if (cell != null && row != null)
                {

                    cell.OnSelect = () => row.Файл = cell.Pointer;
                    (row.Сhanged_Файл = () => cell.Pointer = row.Файл).Invoke();

                }
            };
            ColumnViewColumn column = ColumnViewColumn.New("Файл", factory);
            column.Resizable = true;

            column.FixedWidth = 500;

            Grid.AppendColumn(column);
        }

        { /* Пуста колонка для заповнення вільного простору */
            ColumnViewColumn column = ColumnViewColumn.New(null, null);
            column.Resizable = true;
            column.Expand = true;
            Grid.AppendColumn(column);
        }
    }

    public override async ValueTask LoadRecords()
    {

        if (ЕлементВласник != null) 
        {

            ЕлементВласник.Файли_TablePart.FillJoin([Номенклатура_Файли_TablePart.НомерРядка,]);
            await ЕлементВласник.Файли_TablePart.Read();


        Store.RemoveAll();


        foreach (Номенклатура_Файли_TablePart.Record record in ЕлементВласник.Файли_TablePart.Records)
        {
            Store.Append(new ItemRow()
            {
                UnigueID = new(record.UID),
                НомерРядка = record.НомерРядка,
                Основний = record.Основний,
                Файл = record.Файл,

            });

            if (SelectPosition > 0)
            {
                Grid.Model.SelectItem(SelectPosition, true);
                ScrollTo(SelectPosition);
            }
        }
        }
    }

    public override async ValueTask SaveRecords()
    {

        if (ЕлементВласник != null)
        {
        ЕлементВласник.Файли_TablePart.Records.Clear();
        for (uint i = 0; i <= Store.GetNItems(); i++)
        {
            ItemRow? row = (ItemRow?)Store.GetObject(i);
            if (row != null)
            {
                ЕлементВласник.Файли_TablePart.Records.Add(new()
                {
                    UID = row.UnigueID.UGuid,
                    НомерРядка = row.НомерРядка,
                    Основний = row.Основний,
                    Файл = row.Файл,

                });
            }
        }
        await ЕлементВласник.Файли_TablePart.Save(true);
        await LoadRecords();
        }
    }

    public override bool NewRecord()
    {
        Store.Append(new ItemRow());
        return true;
    }
}

/*
        Номенклатура_Функції.cs
        Функції
*/

using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Довідники;

namespace StorageAndTrade;

static class Номенклатура_Функції
{
    public static List<Where> Відбори(string searchText)
    {
        return
        [

            //Назва
            new Where(Номенклатура_Const.Назва, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //Код
            new Where(Comparison.OR, Номенклатура_Const.Код, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //НазваПовна
            new Where(Comparison.OR, Номенклатура_Const.НазваПовна, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //Опис
            new Where(Comparison.OR, Номенклатура_Const.Опис, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //Артикул
            new Where(Comparison.OR, Номенклатура_Const.Артикул, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

        ];
    }

    public static async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null, 
        Action<UnigueID?>? сallBack_LoadRecords = null, 
        Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        Номенклатура_Елемент page = new()
        {
            CallBack_LoadRecords = сallBack_LoadRecords,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        if (IsNew)
        {
            await page.Елемент.New();

        }
        else if (unigueID == null || !await page.Елемент.Read(unigueID))
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return;
        }

        Program.BasicForm?.NotebookFunc.CreatePage(page.Caption, page);
        await page.SetValue();
    }

    public static async ValueTask OpenPageList(UnigueID? unigueID = null, Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        Номенклатура_Список page = new()
        {
            DirectoryPointerItem = unigueID,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        Program.BasicForm?.NotebookFunc.CreatePage(Номенклатура_Const.FULLNAME, page);
        await page.SetValue();
    }

    public static async ValueTask SetDeletionLabel(UnigueID unigueID)
    {
        Номенклатура_Pointer Вказівник = new(unigueID);
        bool? label = await Вказівник.GetDeletionLabel();
        if (label.HasValue) await Вказівник.SetDeletionLabel(!label.Value);
    }

    public static async ValueTask<UnigueID?> Copy(UnigueID unigueID)
    {
        Номенклатура_Objest Обєкт = new();
        if (await Обєкт.Read(unigueID))
        {
            Номенклатура_Objest Новий = await Обєкт.Copy(true);
            await Новий.Save();

                await Новий.Файли_TablePart.Save(false); // Таблична частина "Файли"

            return Новий.UnigueID;
        }
        else
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return null;
        }
    }
}

/*     
        Номенклатура.cs 
        Список з Деревом
*/

using Gtk;
using InterfaceGtk;
using AccountingSoftware;
using GeneratedCode;
using GeneratedCode.Довідники;
using ТабличніСписки = GeneratedCode.Довідники.ТабличніСписки;

namespace StorageAndTrade
{
    public class Номенклатура : ДовідникЖурнал
    {
        CheckButton checkButtonIsHierarchy = new CheckButton("Ієрархія папок") { Active = true };
        Номенклатура_Папки ДеревоПапок;


        public Номенклатура()
        {
            //Враховувати ієрархію папок
            checkButtonIsHierarchy.Clicked += async (object? sender, EventArgs args) => await LoadRecords();
            HBoxTop.PackStart(checkButtonIsHierarchy, false, false, 10);

            //Дерево папок
            ДеревоПапок = new Номенклатура_Папки() { WidthRequest = 500, CallBack_RowActivated = LoadRecords_TreeCallBack };
            HPanedTable.Pack2(ДеревоПапок, false, true);

            ТабличніСписки.Номенклатура_Записи.AddColumns(TreeViewGrid);
            Config.Kernel.DirectoryObjectChanged += async (object? sender, Dictionary<string, List<Guid>> directory) =>
            {
                if (directory.Any((x) => x.Key == Номенклатура_Const.TYPE))
                    await LoadRecords();
            };


        }

        #region Override

        protected override async ValueTask LoadRecords()
        {
            if (DirectoryPointerItem != null || SelectPointerItem != null)
            {
                Номенклатура_Objest? Обєкт = await new Номенклатура_Pointer(SelectPointerItem ?? DirectoryPointerItem ?? new UnigueID()).GetDirectoryObject();
                if (Обєкт != null) ДеревоПапок.SelectPointerItem = Обєкт.Папка.UnigueID;
            }

            await ДеревоПапок.SetValue();
        }

        async void LoadRecords_TreeCallBack()
        {
            ТабличніСписки.Номенклатура_Записи.SelectPointerItem = SelectPointerItem;
            ТабличніСписки.Номенклатура_Записи.DirectoryPointerItem = DirectoryPointerItem;

            ТабличніСписки.Номенклатура_Записи.ОчиститиВідбір(TreeViewGrid);

            if (checkButtonIsHierarchy.Active)
                ТабличніСписки.Номенклатура_Записи.ДодатиВідбір(TreeViewGrid,
                    new Where(Номенклатура_Const.Папка, Comparison.EQ, ДеревоПапок.SelectPointerItem?.UGuid ?? new UnigueID().UGuid));

            await ТабличніСписки.Номенклатура_Записи.LoadRecords(TreeViewGrid, OpenFolder);
        }

        protected override async ValueTask LoadRecords_OnSearch(string searchText)
        {
            ТабличніСписки.Номенклатура_Записи.ОчиститиВідбір(TreeViewGrid);

            //Відбори
            ТабличніСписки.Номенклатура_Записи.ДодатиВідбір(TreeViewGrid, Номенклатура_Функції.Відбори(searchText));

            await ТабличніСписки.Номенклатура_Записи.LoadRecords(TreeViewGrid, OpenFolder);
        }

        protected override Widget? FilterRecords(Box hBox)
        {
            return ТабличніСписки.Номенклатура_Записи.CreateFilter(TreeViewGrid);
        }

        protected override async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null)
        {
            await  Номенклатура_Функції.OpenPageElement(IsNew, unigueID, CallBack_LoadRecords, null);

        }

        protected override async ValueTask SetDeletionLabel(UnigueID unigueID)
        {
            await  Номенклатура_Функції.SetDeletionLabel(unigueID);

        }

        protected override async ValueTask<UnigueID?> Copy(UnigueID unigueID)
        {
            return await  Номенклатура_Функції.Copy(unigueID);

        }

        #endregion
    }
}

/*     
        Номенклатура_ШвидкийВибір.cs
        ШвидкийВибір
*/

using Gtk;
using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Довідники;

using ТабличнийСписок = GeneratedCode.Довідники.ТабличніСписки.Номенклатура_ЗаписиМіні;
using Функції = StorageAndTrade.Номенклатура_Функції;

namespace StorageAndTrade;

class Номенклатура_ШвидкийВибір : DirectoryFormJournalSmall
{


    public Номенклатура_ШвидкийВибір() : base(Program.BasicForm?.NotebookFunc)
    {
        TypeName = Номенклатура_Const.POINTER;
        KeyForSetting = ".Small";
        ТабличнийСписок.AddColumn(this);
        SetPagesSettings(50);
    }

    public override async ValueTask LoadRecords()
    {
        await ТабличнийСписок.LoadRecords(this);
    }

    public override async ValueTask UpdateRecords()
    {
        await ТабличнийСписок.UpdateRecords(this);
    }

    protected override async void SetSearch(string searchText)
    {
        WhereList = Функції.Відбори(searchText);
    }

    protected override void FillFilter(FilterControl filterControl)
    {
        ТабличнийСписок.CreateFilter(this);
    }

    protected override async ValueTask OpenPageList(UnigueID? unigueID = null)
    {
        await Функції.OpenPageList(unigueID, CallBack_OnSelectPointer);
    }

    protected override async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null)
    {
        await Функції.OpenPageElement(IsNew, unigueID, CallBack_LoadRecords, CallBack_OnSelectPointer);
    }

    protected override async ValueTask SetDeletionLabel(UnigueID unigueID)
    {
        await Функції.SetDeletionLabel(unigueID);
    }

    protected override async ValueTask<UnigueID?> Copy(UnigueID unigueID)
    {
        return await Функції.Copy(unigueID);
    }
}

