/*
        ФізичніОсоби_Елемент.cs
        Елемент
*/

using Gtk;
using InterfaceGtk4;

using GeneratedCode.Довідники;
using GeneratedCode.Документи;
using GeneratedCode.Перелічення;

namespace StorageAndTrade;

class ФізичніОсоби_Елемент : DirectoryFormElement
{
    public ФізичніОсоби_Objest Елемент { get; init; } = new ФізичніОсоби_Objest();

    #region Fields
    Entry Код = new() { WidthRequest = 100 };
                    Entry Назва = new() { WidthRequest = 500 };
                    DateTimeControl ДатаНародження = new(){ OnlyDate = true };
            ComboBoxText Стать = new ComboBoxText();
            Entry ІПН = new() { WidthRequest = 200 };

    #endregion

    #region TabularParts

        // Таблична частина "Контакти"
        ФізичніОсоби_ТабличнаЧастина_Контакти Контакти = new() { WidthRequest = 500, HeightRequest = 300 };

    #endregion

    public ФізичніОсоби_Елемент() : base(Program.BasicForm?.NotebookFunc)
    { 
        Element = Елемент;

        {
            //Заповнення списку
            foreach (var field in ПсевдонімиПерелічення.СтатьФізичноїОсоби_List())
                Стать.Append(field.Value.ToString(), field.Name);

            //Заборона прокрутки списку
            EventControllerScroll controller = EventControllerScroll.New(EventControllerScrollFlags.BothAxes);
            Стать.AddController(controller);
            controller.OnScroll += (_, _) => true;
        }

    }

    protected override void CreateStart(Box vBox)
    {

            // Код
            CreateField(vBox, "Код:", Код);

            // Назва
            CreateField(vBox, "Назва:", Назва);

            // ДатаНародження
            CreateField(vBox, "Дата народження:", ДатаНародження);

            // Стать
            CreateField(vBox, "Стать:", Стать);

            // ІПН
            CreateField(vBox, "ІПН:", ІПН);

    }

    protected override void CreateEnd(Box vBox)
    {

            // Таблична частина "Контакти" 
            CreateTablePart(vBox, "Контакти:", Контакти);

    }

    #region Присвоєння / зчитування значень

    public override async ValueTask AssignValue()
    {
        Код.SetText(Елемент.Код);
                        Назва.SetText(Елемент.Назва);
                        ДатаНародження.Value = Елемент.ДатаНародження;
                Стать.ActiveId = Елемент.Стать.ToString();
                ІПН.SetText(Елемент.ІПН);

            // Таблична частина "Контакти"
            Контакти.ЕлементВласник = Елемент;
            await Контакти.LoadRecords();

    }

    protected override void GetValue()
    {
        Елемент.Код = Код.GetText();
                        Елемент.Назва = Назва.GetText();
                        Елемент.ДатаНародження = ДатаНародження.Value;
                Елемент.Стать = ПсевдонімиПерелічення.СтатьФізичноїОсоби_FindByName(Стать.ActiveId);
                Елемент.ІПН = ІПН.GetText();

    }

    #endregion

    protected override async ValueTask<bool> Save()
    {
        bool isSaved = false;
        try
        {
            if (await Елемент.Save())
            {
                await Контакти.SaveRecords(); // Таблична частина "Контакти"

                isSaved = true;
            }
        }
        catch (Exception ex)
        {
            //ФункціїДляПовідомлень.ДодатиПовідомлення(Елемент.GetBasis(), Caption, ex);
        }
        return isSaved;
    }
}

/*     
        ФізичніОсоби_PointerControl.cs
        PointerControl
*/

using Gtk;
using InterfaceGtk4;
using GeneratedCode.Довідники;

namespace StorageAndTrade;

public class ФізичніОсоби_PointerControl : PointerControl
{
    event EventHandler<ФізичніОсоби_Pointer> PointerChanged;

    public ФізичніОсоби_PointerControl()
    {
        pointer = new ФізичніОсоби_Pointer();
        WidthPresentation = 300;
        Caption = $"{ФізичніОсоби_Const.FULLNAME}:";
        PointerChanged += async (_, pointer) => Presentation = pointer != null ? await pointer.GetPresentation() : "";
    }

    ФізичніОсоби_Pointer pointer;
    public ФізичніОсоби_Pointer Pointer
    {
        get
        {
            return pointer;
        }
        set
        {
            pointer = value;
            PointerChanged?.Invoke(null, pointer);
        }
    }



    protected override async void OpenSelect(Button button, EventArgs args)
    {
        Popover popover = Popover.New();
        popover.SetParent(button);
        popover.WidthRequest = 800;
        popover.HeightRequest = 400;
        BeforeClickOpenFunc?.Invoke();
        ФізичніОсоби_ШвидкийВибір page = new()
        {
            PopoverParent = popover,
            DirectoryPointerItem = Pointer.UnigueID,
            CallBack_OnSelectPointer = selectPointer =>
            {
                Pointer = new ФізичніОсоби_Pointer(selectPointer);
                AfterSelectFunc?.Invoke();
            }
        };

        popover.SetChild(page);
        popover.Show();

        await page.SetValue();
    }

    protected override void OnClear(Button button, EventArgs args)
    {
        Pointer = new ФізичніОсоби_Pointer();
        AfterSelectFunc?.Invoke();
        AfterClearFunc?.Invoke();
    }
}

/*     
        ФізичніОсоби_PointerTablePartCell.cs
        PointerTablePartCell
*/
using Gtk;
using InterfaceGtk4;
using GeneratedCode.Довідники;
using AccountingSoftware;

namespace StorageAndTrade;

public class ФізичніОсоби_PointerTablePartCell : PointerTablePartCell
{
    ФізичніОсоби_Pointer pointer = new();
    public ФізичніОсоби_Pointer Pointer
    {
        get => pointer;
        set
        {
            pointer = value;
            Presentation = pointer.Name;
        }
    }

    public async ValueTask GetPresentation() => Presentation = pointer != null ? await pointer.GetPresentation() : "";

    async ValueTask PointerChange(UnigueID? p)
    {
        Pointer = new ФізичніОсоби_Pointer(p ?? new UnigueID());
        await GetPresentation();
        OnSelect?.Invoke();
    }



    protected override async void Select(Button button, EventArgs args)
    {
        Popover popover = Popover.New();
        popover.SetParent(button);
        popover.WidthRequest = 800;
        popover.HeightRequest = 400;

        ФізичніОсоби_ШвидкийВибір page = new()
        {
            PopoverParent = popover,
            DirectoryPointerItem = pointer.UnigueID,
            CallBack_OnSelectPointer = async p => await PointerChange(p)
        };


        popover.SetChild(page);
        popover.Show();

        await page.SetValue();
    }

    protected override async void Clear()
    {
        await PointerChange(null);
    }
}

/*
        СтруктураПідприємства_Функції.cs
        Функції
*/

using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Довідники;

namespace StorageAndTrade;

static class СтруктураПідприємства_Функції
{
    public static List<Where> Відбори(string searchText)
    {
        return
        [

            //Код
            new Where(СтруктураПідприємства_Const.Код, Comparison.LIKE, searchText) { FuncToField = "TO_CHAR", FuncToField_Param1 = "''" },

            //Назва
            new Where(Comparison.OR, СтруктураПідприємства_Const.Назва, Comparison.LIKE, searchText) { FuncToField = "TO_CHAR", FuncToField_Param1 = "''" },

        ];
    }

    public static async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null, 
        Action<UnigueID?>? сallBack_LoadRecords = null, 
        Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        СтруктураПідприємства_Елемент page = new()
        {
            CallBack_LoadRecords = сallBack_LoadRecords,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        if (IsNew)
        {
            await page.Елемент.New();

        }
        else if (unigueID == null || !await page.Елемент.Read(unigueID))
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return;
        }

        Program.BasicForm?.NotebookFunc.CreatePage(page.Caption, page);
        await page.SetValue();
    }

    public static async ValueTask OpenPageList(UnigueID? unigueID = null, Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        СтруктураПідприємства_Список page = new()
        {
            DirectoryPointerItem = unigueID,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        Program.BasicForm?.NotebookFunc.CreatePage(СтруктураПідприємства_Const.FULLNAME, page);
        await page.SetValue();
    }

    public static async ValueTask SetDeletionLabel(UnigueID unigueID)
    {
        СтруктураПідприємства_Pointer Вказівник = new(unigueID);
        bool? label = await Вказівник.GetDeletionLabel();
        if (label.HasValue) await Вказівник.SetDeletionLabel(!label.Value);
    }

    public static async ValueTask<UnigueID?> Copy(UnigueID unigueID)
    {
        СтруктураПідприємства_Objest Обєкт = new();
        if (await Обєкт.Read(unigueID))
        {
            СтруктураПідприємства_Objest Новий = await Обєкт.Copy(true);
            await Новий.Save();

            return Новий.UnigueID;
        }
        else
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return null;
        }
    }
}

