using Gtk;
using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Довідники;

using ТабличнийСписок = GeneratedCode.Довідники.ТабличніСписки.ДоговориКонтрагентів_ЗаписиШвидкийВибір;
using Функції = StorageAndTrade.ДоговориКонтрагентів_Функції;

namespace StorageAndTrade;

class ДоговориКонтрагентів_ШвидкийВибір : DirectoryFormJournalSmall
{

    public Контрагенти_PointerControl Власник = new Контрагенти_PointerControl() { Caption = "Контрагент:" };


    public ДоговориКонтрагентів_ШвидкийВибір() : base(Program.BasicForm?.NotebookFunc)
    {
        TypeName = ДоговориКонтрагентів_Const.POINTER;
        KeyForSetting = ".Small";
        ТабличнийСписок.AddColumn(this);
        SetPagesSettings(50);
    }

    public override async ValueTask LoadRecords()
    {
        await ТабличнийСписок.LoadRecords(this);
    }

    public override async ValueTask UpdateRecords()
    {
        await ТабличнийСписок.UpdateRecords(this);
    }

    protected override async void SetSearch(string searchText)
    {
        WhereList = Функції.Відбори(searchText);
    }

    protected override void FillFilter(FilterControl filterControl)
    {
        ТабличнийСписок.CreateFilter(this);
    }

    protected override async ValueTask OpenPageList(UnigueID? unigueID = null)
    {
        await Функції.OpenPageList(unigueID, CallBack_OnSelectPointer);
    }

    protected override async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null)
    {
        await Функції.OpenPageElement(IsNew, unigueID, CallBack_LoadRecords, CallBack_OnSelectPointer);
    }

    protected override async ValueTask SetDeletionLabel(UnigueID unigueID)
    {
        await Функції.SetDeletionLabel(unigueID);
    }

    protected override async ValueTask<UnigueID?> Copy(UnigueID unigueID)
    {
        return await Функції.Copy(unigueID);
    }
}

/*     
        ДоговориКонтрагентів_PointerControl.cs
        PointerControl
*/

using Gtk;
using InterfaceGtk4;
using GeneratedCode.Довідники;

namespace StorageAndTrade;

public class ДоговориКонтрагентів_PointerControl : PointerControl
{
    event EventHandler<ДоговориКонтрагентів_Pointer> PointerChanged;

    public ДоговориКонтрагентів_PointerControl()
    {
        pointer = new ДоговориКонтрагентів_Pointer();
        WidthPresentation = 300;
        Caption = $"{ДоговориКонтрагентів_Const.FULLNAME}:";
        PointerChanged += async (_, pointer) => Presentation = pointer != null ? await pointer.GetPresentation() : "";
    }

    ДоговориКонтрагентів_Pointer pointer;
    public ДоговориКонтрагентів_Pointer Pointer
    {
        get
        {
            return pointer;
        }
        set
        {
            pointer = value;
            PointerChanged?.Invoke(null, pointer);
        }
    }


    public Контрагенти_Pointer Власник { get; set; } = new Контрагенти_Pointer();


    protected override async void OpenSelect(Button button, EventArgs args)
    {
        Popover popover = Popover.New();
        popover.SetParent(button);
        popover.WidthRequest = 800;
        popover.HeightRequest = 400;
        BeforeClickOpenFunc?.Invoke();
        ДоговориКонтрагентів_ШвидкийВибір page = new()
        {
            PopoverParent = popover,
            DirectoryPointerItem = Pointer.UnigueID,
            CallBack_OnSelectPointer = selectPointer =>
            {
                Pointer = new ДоговориКонтрагентів_Pointer(selectPointer);
                AfterSelectFunc?.Invoke();
            }
        };

        page.Власник.Pointer = Власник;

        popover.SetChild(page);
        popover.Show();

        await page.SetValue();
    }

    protected override void OnClear(Button button, EventArgs args)
    {
        Pointer = new ДоговориКонтрагентів_Pointer();
        AfterSelectFunc?.Invoke();
        AfterClearFunc?.Invoke();
    }
}

/*     
        ДоговориКонтрагентів_PointerTablePartCell.cs
        PointerTablePartCell
*/
using Gtk;
using InterfaceGtk4;
using GeneratedCode.Довідники;
using AccountingSoftware;

namespace StorageAndTrade;

public class ДоговориКонтрагентів_PointerTablePartCell : PointerTablePartCell
{
    ДоговориКонтрагентів_Pointer pointer = new();
    public ДоговориКонтрагентів_Pointer Pointer
    {
        get => pointer;
        set
        {
            pointer = value;
            Presentation = pointer.Name;
        }
    }

    public async ValueTask GetPresentation() => Presentation = pointer != null ? await pointer.GetPresentation() : "";

    async ValueTask PointerChange(UnigueID? p)
    {
        Pointer = new ДоговориКонтрагентів_Pointer(p ?? new UnigueID());
        await GetPresentation();
        OnSelect?.Invoke();
    }


    public Контрагенти_Pointer Власник { get; set; } = new Контрагенти_Pointer();


    protected override async void Select(Button button, EventArgs args)
    {
        Popover popover = Popover.New();
        popover.SetParent(button);
        popover.WidthRequest = 800;
        popover.HeightRequest = 400;

        ДоговориКонтрагентів_ШвидкийВибір page = new()
        {
            PopoverParent = popover,
            DirectoryPointerItem = pointer.UnigueID,
            CallBack_OnSelectPointer = async p => await PointerChange(p)
        };

        page.Власник.Pointer = Власник;


        popover.SetChild(page);
        popover.Show();

        await page.SetValue();
    }

    protected override async void Clear()
    {
        await PointerChange(null);
    }
}

/*
        ДоговориКонтрагентів_Елемент.cs
        Елемент
*/

using Gtk;
using InterfaceGtk4;

using GeneratedCode.Довідники;
using GeneratedCode.Документи;
using GeneratedCode.Перелічення;

namespace StorageAndTrade;

class ДоговориКонтрагентів_Елемент : DirectoryFormElement
{
    public ДоговориКонтрагентів_Objest Елемент { get; init; } = new ДоговориКонтрагентів_Objest();

    public Контрагенти_Pointer ВласникДляНового = new Контрагенти_Pointer();

    #region Fields
    Entry Код = new() { WidthRequest = 100 };
                    Entry Назва = new() { WidthRequest = 500 };
                    БанківськіРахункиОрганізацій_PointerControl БанківськийРахунок = new() { Caption = "Банківський рахунок", WidthPresentation = 500 };
            БанківськіРахункиКонтрагентів_PointerControl БанківськийРахунокКонтрагента = new() { Caption = "Банківський рахунок контрагента", WidthPresentation = 500 };
            Валюти_PointerControl ВалютаВзаєморозрахунків = new() { Caption = "Валюта", WidthPresentation = 500 };
            DateTimeControl ДатаПочаткуДії = new(){ OnlyDate = true };
            DateTimeControl ДатаЗакінченняДії = new(){ OnlyDate = true };
            Організації_PointerControl Організація = new() { Caption = "Організація", WidthPresentation = 500 };
            Контрагенти_PointerControl Контрагент = new() { Caption = "Контрагент", WidthPresentation = 500 };
            DateTimeControl Дата = new(){ OnlyDate = true };
            Entry Номер = new() { WidthRequest = 500 };
                    СтруктураПідприємства_PointerControl Підрозділ = new() { Caption = "Підрозділ", WidthPresentation = 500 };
            CheckButton Узгоджений = CheckButton.NewWithLabel("Узгоджений");
            ComboBoxText Статус = new ComboBoxText();
            ComboBoxText ГосподарськаОперація = new ComboBoxText();
            ComboBoxText ТипДоговору = new ComboBoxText();
            NumericControl ДопустимаСумаЗаборгованості = new();
            NumericControl Сума = new();
            Entry Коментар = new() { WidthRequest = 500 };

    #endregion

    #region TabularParts

    #endregion

    public ДоговориКонтрагентів_Елемент() : base(Program.BasicForm?.NotebookFunc)
    { 
        Element = Елемент;

        {
            //Заповнення списку
            foreach (var field in ПсевдонімиПерелічення.СтатусиДоговорівКонтрагентів_List())
                Статус.Append(field.Value.ToString(), field.Name);

            //Заборона прокрутки списку
            EventControllerScroll controller = EventControllerScroll.New(EventControllerScrollFlags.BothAxes);
            Статус.AddController(controller);
            controller.OnScroll += (_, _) => true;
        }

        {
            //Заповнення списку
            foreach (var field in ПсевдонімиПерелічення.ГосподарськіОперації_List())
                ГосподарськаОперація.Append(field.Value.ToString(), field.Name);

            //Заборона прокрутки списку
            EventControllerScroll controller = EventControllerScroll.New(EventControllerScrollFlags.BothAxes);
            ГосподарськаОперація.AddController(controller);
            controller.OnScroll += (_, _) => true;
        }

        {
            //Заповнення списку
            foreach (var field in ПсевдонімиПерелічення.ТипДоговорів_List())
                ТипДоговору.Append(field.Value.ToString(), field.Name);

            //Заборона прокрутки списку
            EventControllerScroll controller = EventControllerScroll.New(EventControllerScrollFlags.BothAxes);
            ТипДоговору.AddController(controller);
            controller.OnScroll += (_, _) => true;
        }

    }

    protected override void CreateStart(Box vBox)
    {

            // Код
            CreateField(vBox, "Код:", Код);

            // Назва
            CreateField(vBox, "Назва:", Назва);

            // БанківськийРахунок
            CreateField(vBox, null, БанківськийРахунок);

            // БанківськийРахунокКонтрагента
            CreateField(vBox, null, БанківськийРахунокКонтрагента);

            // ВалютаВзаєморозрахунків
            CreateField(vBox, null, ВалютаВзаєморозрахунків);

            // ДатаПочаткуДії
            CreateField(vBox, "Дата початку:", ДатаПочаткуДії);

            // ДатаЗакінченняДії
            CreateField(vBox, "Дата закінчення:", ДатаЗакінченняДії);

            // Організація
            CreateField(vBox, null, Організація);

            // Контрагент
            CreateField(vBox, null, Контрагент);

            // Дата
            CreateField(vBox, "Дата:", Дата);

            // Номер
            CreateField(vBox, "Номер:", Номер);

            // Підрозділ
            CreateField(vBox, null, Підрозділ);

            // Узгоджений
            CreateField(vBox, null, Узгоджений);

            // Статус
            CreateField(vBox, "Статус:", Статус);

            // ГосподарськаОперація
            CreateField(vBox, "ГосподарськаОперація:", ГосподарськаОперація);

            // ТипДоговору
            CreateField(vBox, "ТипДоговору:", ТипДоговору);

            // ДопустимаСумаЗаборгованості
            CreateField(vBox, "Допустима сума заборгованості:", ДопустимаСумаЗаборгованості);

            // Сума
            CreateField(vBox, "Сума:", Сума);

            // Коментар
            CreateField(vBox, "Коментар:", Коментар);

    }

    protected override void CreateEnd(Box vBox)
    {

    }

    #region Присвоєння / зчитування значень

    public override async ValueTask AssignValue()
    {

        if (IsNew) 
            Елемент.Контрагент = ВласникДляНового;
        Код.SetText(Елемент.Код);
                        Назва.SetText(Елемент.Назва);
                        БанківськийРахунок.Pointer = Елемент.БанківськийРахунок;
                БанківськийРахунокКонтрагента.Pointer = Елемент.БанківськийРахунокКонтрагента;
                ВалютаВзаєморозрахунків.Pointer = Елемент.ВалютаВзаєморозрахунків;
                ДатаПочаткуДії.Value = Елемент.ДатаПочаткуДії;
                ДатаЗакінченняДії.Value = Елемент.ДатаЗакінченняДії;
                Організація.Pointer = Елемент.Організація;
                Контрагент.Pointer = Елемент.Контрагент;
                Дата.Value = Елемент.Дата;
                Номер.SetText(Елемент.Номер);
                        Підрозділ.Pointer = Елемент.Підрозділ;
                Узгоджений.Active = Елемент.Узгоджений;
                Статус.ActiveId = Елемент.Статус.ToString();
                ГосподарськаОперація.ActiveId = Елемент.ГосподарськаОперація.ToString();
                ТипДоговору.ActiveId = Елемент.ТипДоговору.ToString();
                ДопустимаСумаЗаборгованості.Value = Елемент.ДопустимаСумаЗаборгованості;
                Сума.Value = Елемент.Сума;
                Коментар.SetText(Елемент.Коментар);

    }

    protected override void GetValue()
    {
        Елемент.Код = Код.GetText();
                        Елемент.Назва = Назва.GetText();
                        Елемент.БанківськийРахунок = БанківськийРахунок.Pointer;
                Елемент.БанківськийРахунокКонтрагента = БанківськийРахунокКонтрагента.Pointer;
                Елемент.ВалютаВзаєморозрахунків = ВалютаВзаєморозрахунків.Pointer;
                Елемент.ДатаПочаткуДії = ДатаПочаткуДії.Value;
                Елемент.ДатаЗакінченняДії = ДатаЗакінченняДії.Value;
                Елемент.Організація = Організація.Pointer;
                Елемент.Контрагент = Контрагент.Pointer;
                Елемент.Дата = Дата.Value;
                Елемент.Номер = Номер.GetText();
                        Елемент.Підрозділ = Підрозділ.Pointer;
                Елемент.Узгоджений = Узгоджений.Active;
                Елемент.Статус = ПсевдонімиПерелічення.СтатусиДоговорівКонтрагентів_FindByName(Статус.ActiveId);
                Елемент.ГосподарськаОперація = ПсевдонімиПерелічення.ГосподарськіОперації_FindByName(ГосподарськаОперація.ActiveId);
                Елемент.ТипДоговору = ПсевдонімиПерелічення.ТипДоговорів_FindByName(ТипДоговору.ActiveId);
                Елемент.ДопустимаСумаЗаборгованості = ДопустимаСумаЗаборгованості.Value;
                Елемент.Сума = Сума.Value;
                Елемент.Коментар = Коментар.GetText();

    }

    #endregion

    protected override async ValueTask<bool> Save()
    {
        bool isSaved = false;
        try
        {
            if (await Елемент.Save())
            {

                isSaved = true;
            }
        }
        catch (Exception ex)
        {
            //ФункціїДляПовідомлень.ДодатиПовідомлення(Елемент.GetBasis(), Caption, ex);
        }
        return isSaved;
    }
}

/*
        БанківськіРахункиКонтрагентів_Функції.cs
        Функції
*/

using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Довідники;

namespace StorageAndTrade;

static class БанківськіРахункиКонтрагентів_Функції
{
    public static List<Where> Відбори(string searchText)
    {
        return
        [

            //Код
            new Where(БанківськіРахункиКонтрагентів_Const.Код, Comparison.LIKE, searchText) { FuncToField = "TO_CHAR", FuncToField_Param1 = "''" },

            //Назва
            new Where(Comparison.OR, БанківськіРахункиКонтрагентів_Const.Назва, Comparison.LIKE, searchText) { FuncToField = "TO_CHAR", FuncToField_Param1 = "''" },

        ];
    }

    public static async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null, 
        Action<UnigueID?>? сallBack_LoadRecords = null, 
        Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        БанківськіРахункиКонтрагентів_Елемент page = new()
        {
            CallBack_LoadRecords = сallBack_LoadRecords,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        if (IsNew)
        {
            await page.Елемент.New();

        }
        else if (unigueID == null || !await page.Елемент.Read(unigueID))
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return;
        }

        Program.BasicForm?.NotebookFunc.CreatePage(page.Caption, page);
        await page.SetValue();
    }

    public static async ValueTask OpenPageList(UnigueID? unigueID = null, Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        БанківськіРахункиКонтрагентів_Список page = new()
        {
            DirectoryPointerItem = unigueID,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        Program.BasicForm?.NotebookFunc.CreatePage(БанківськіРахункиКонтрагентів_Const.FULLNAME, page);
        await page.SetValue();
    }

    public static async ValueTask SetDeletionLabel(UnigueID unigueID)
    {
        БанківськіРахункиКонтрагентів_Pointer Вказівник = new(unigueID);
        bool? label = await Вказівник.GetDeletionLabel();
        if (label.HasValue) await Вказівник.SetDeletionLabel(!label.Value);
    }

    public static async ValueTask<UnigueID?> Copy(UnigueID unigueID)
    {
        БанківськіРахункиКонтрагентів_Objest Обєкт = new();
        if (await Обєкт.Read(unigueID))
        {
            БанківськіРахункиКонтрагентів_Objest Новий = await Обєкт.Copy(true);
            await Новий.Save();

            return Новий.UnigueID;
        }
        else
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return null;
        }
    }
}

