/*
        ПартіяТоварівКомпозит_Елемент.cs
        Елемент
*/

using Gtk;
using InterfaceGtk4;

using GeneratedCode.Довідники;
using GeneratedCode.Документи;
using GeneratedCode.Перелічення;

namespace StorageAndTrade;

class ПартіяТоварівКомпозит_Елемент : DirectoryFormElement
{
    public ПартіяТоварівКомпозит_Objest Елемент { get; init; } = new ПартіяТоварівКомпозит_Objest();

    #region Fields
    Entry Назва = new() { WidthRequest = 500 };
                    DateTimeControl Дата = new();
            ComboBoxText ТипДокументу = new ComboBoxText();
            ПоступленняТоварівТаПослуг_PointerControl ПоступленняТоварівТаПослуг = new() { Caption = "Поступлення товарів та послуг", WidthPresentation = 500 };
            ВведенняЗалишків_PointerControl ВведенняЗалишків = new() { Caption = "Введення залишків", WidthPresentation = 500 };

    #endregion

    #region TabularParts

    #endregion

    public ПартіяТоварівКомпозит_Елемент() : base(Program.BasicForm?.NotebookFunc)
    { 
        Element = Елемент;

        {
            //Заповнення списку
            foreach (var field in ПсевдонімиПерелічення.ТипДокументуПартіяТоварівКомпозит_List())
                ТипДокументу.Append(field.Value.ToString(), field.Name);

            //Заборона прокрутки списку
            EventControllerScroll controller = EventControllerScroll.New(EventControllerScrollFlags.BothAxes);
            ТипДокументу.AddController(controller);
            controller.OnScroll += (_, _) => true;
        }

    }

    protected override void CreateStart(Box vBox)
    {

            // Назва
            CreateField(vBox, "Назва:", Назва);

            // Дата
            CreateField(vBox, "Дата:", Дата);

            // ТипДокументу
            CreateField(vBox, "Тип документу:", ТипДокументу);

            // ПоступленняТоварівТаПослуг
            CreateField(vBox, null, ПоступленняТоварівТаПослуг);

            // ВведенняЗалишків
            CreateField(vBox, null, ВведенняЗалишків);

    }

    protected override void CreateEnd(Box vBox)
    {

    }

    #region Присвоєння / зчитування значень

    public override async ValueTask AssignValue()
    {
        Назва.SetText(Елемент.Назва);
                        Дата.Value = Елемент.Дата;
                ТипДокументу.ActiveId = Елемент.ТипДокументу.ToString();
                ПоступленняТоварівТаПослуг.Pointer = Елемент.ПоступленняТоварівТаПослуг;
                ВведенняЗалишків.Pointer = Елемент.ВведенняЗалишків;

    }

    protected override void GetValue()
    {
        Елемент.Назва = Назва.GetText();
                        Елемент.Дата = Дата.Value;
                Елемент.ТипДокументу = ПсевдонімиПерелічення.ТипДокументуПартіяТоварівКомпозит_FindByName(ТипДокументу.ActiveId);
                Елемент.ПоступленняТоварівТаПослуг = ПоступленняТоварівТаПослуг.Pointer;
                Елемент.ВведенняЗалишків = ВведенняЗалишків.Pointer;

    }

    #endregion

    protected override async ValueTask<bool> Save()
    {
        bool isSaved = false;
        try
        {
            if (await Елемент.Save())
            {

                isSaved = true;
            }
        }
        catch (Exception ex)
        {
            //ФункціїДляПовідомлень.ДодатиПовідомлення(Елемент.GetBasis(), Caption, ex);
        }
        return isSaved;
    }
}

/*
        ВидиЗапасів_Функції.cs
        Функції
*/

using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Довідники;

namespace StorageAndTrade;

static class ВидиЗапасів_Функції
{
    public static List<Where> Відбори(string searchText)
    {
        return
        [

            //Код
            new Where(ВидиЗапасів_Const.Код, Comparison.LIKE, searchText) { FuncToField = "TO_CHAR", FuncToField_Param1 = "''" },

            //Назва
            new Where(Comparison.OR, ВидиЗапасів_Const.Назва, Comparison.LIKE, searchText) { FuncToField = "TO_CHAR", FuncToField_Param1 = "''" },

        ];
    }

    public static async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null, 
        Action<UnigueID?>? сallBack_LoadRecords = null, 
        Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        ВидиЗапасів_Елемент page = new()
        {
            CallBack_LoadRecords = сallBack_LoadRecords,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        if (IsNew)
        {
            await page.Елемент.New();

        }
        else if (unigueID == null || !await page.Елемент.Read(unigueID))
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return;
        }

        Program.BasicForm?.NotebookFunc.CreatePage(page.Caption, page);
        await page.SetValue();
    }

    public static async ValueTask OpenPageList(UnigueID? unigueID = null, Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        ВидиЗапасів_Список page = new()
        {
            DirectoryPointerItem = unigueID,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        Program.BasicForm?.NotebookFunc.CreatePage(ВидиЗапасів_Const.FULLNAME, page);
        await page.SetValue();
    }

    public static async ValueTask SetDeletionLabel(UnigueID unigueID)
    {
        ВидиЗапасів_Pointer Вказівник = new(unigueID);
        bool? label = await Вказівник.GetDeletionLabel();
        if (label.HasValue) await Вказівник.SetDeletionLabel(!label.Value);
    }

    public static async ValueTask<UnigueID?> Copy(UnigueID unigueID)
    {
        ВидиЗапасів_Objest Обєкт = new();
        if (await Обєкт.Read(unigueID))
        {
            ВидиЗапасів_Objest Новий = await Обєкт.Copy(true);
            await Новий.Save();

            return Новий.UnigueID;
        }
        else
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return null;
        }
    }
}

