/*     
        ВидиЗапасів_PointerControl.cs
        PointerControl
*/

using Gtk;
using InterfaceGtk4;
using GeneratedCode.Довідники;

namespace StorageAndTrade;

public class ВидиЗапасів_PointerControl : PointerControl
{
    event EventHandler<ВидиЗапасів_Pointer> PointerChanged;

    public ВидиЗапасів_PointerControl()
    {
        pointer = new ВидиЗапасів_Pointer();
        WidthPresentation = 300;
        Caption = $"{ВидиЗапасів_Const.FULLNAME}:";
        PointerChanged += async (_, pointer) => Presentation = pointer != null ? await pointer.GetPresentation() : "";
    }

    ВидиЗапасів_Pointer pointer;
    public ВидиЗапасів_Pointer Pointer
    {
        get
        {
            return pointer;
        }
        set
        {
            pointer = value;
            PointerChanged?.Invoke(null, pointer);
        }
    }



    protected override async void OpenSelect(Button button, EventArgs args)
    {
        Popover popover = Popover.New();
        popover.SetParent(button);
        popover.WidthRequest = 800;
        popover.HeightRequest = 400;
        BeforeClickOpenFunc?.Invoke();
        ВидиЗапасів_ШвидкийВибір page = new()
        {
            PopoverParent = popover,
            DirectoryPointerItem = Pointer.UnigueID,
            CallBack_OnSelectPointer = selectPointer =>
            {
                Pointer = new ВидиЗапасів_Pointer(selectPointer);
                AfterSelectFunc?.Invoke();
            }
        };

        popover.SetChild(page);
        popover.Show();

        await page.SetValue();
    }

    protected override void OnClear(Button button, EventArgs args)
    {
        Pointer = new ВидиЗапасів_Pointer();
        AfterSelectFunc?.Invoke();
        AfterClearFunc?.Invoke();
    }
}

/*
        ВидиЗапасів_Елемент.cs
        Елемент
*/

using Gtk;
using InterfaceGtk4;

using GeneratedCode.Довідники;
using GeneratedCode.Документи;
using GeneratedCode.Перелічення;

namespace StorageAndTrade;

class ВидиЗапасів_Елемент : DirectoryFormElement
{
    public ВидиЗапасів_Objest Елемент { get; init; } = new ВидиЗапасів_Objest();

    #region Fields
    Entry Код = new() { WidthRequest = 100 };
                    Entry Назва = new() { WidthRequest = 500 };
                    Організації_PointerControl Організація = new() { Caption = "Організація", WidthPresentation = 500 };
            ComboBoxText ТипЗапасів = new ComboBoxText();
            Валюти_PointerControl Валюта = new() { Caption = "Валюта", WidthPresentation = 500 };
            Контрагенти_PointerControl Контрагент = new() { Caption = "Контрагент", WidthPresentation = 500 };
            ДоговориКонтрагентів_PointerControl Договір = new() { Caption = "Договір", WidthPresentation = 500 };

    #endregion

    #region TabularParts

    #endregion

    public ВидиЗапасів_Елемент() : base(Program.BasicForm?.NotebookFunc)
    { 
        Element = Елемент;

        {
            //Заповнення списку
            foreach (var field in ПсевдонімиПерелічення.ТипЗапасів_List())
                ТипЗапасів.Append(field.Value.ToString(), field.Name);

            //Заборона прокрутки списку
            EventControllerScroll controller = EventControllerScroll.New(EventControllerScrollFlags.BothAxes);
            ТипЗапасів.AddController(controller);
            controller.OnScroll += (_, _) => true;
        }

    }

    protected override void CreateStart(Box vBox)
    {

            // Код
            CreateField(vBox, "Код:", Код);

            // Назва
            CreateField(vBox, "Назва:", Назва);

            // Організація
            CreateField(vBox, null, Організація);

            // ТипЗапасів
            CreateField(vBox, "Тип запасів:", ТипЗапасів);

            // Валюта
            CreateField(vBox, null, Валюта);

            // Контрагент
            CreateField(vBox, null, Контрагент);

            // Договір
            CreateField(vBox, null, Договір);

    }

    protected override void CreateEnd(Box vBox)
    {

    }

    #region Присвоєння / зчитування значень

    public override async ValueTask AssignValue()
    {
        Код.SetText(Елемент.Код);
                        Назва.SetText(Елемент.Назва);
                        Організація.Pointer = Елемент.Організація;
                ТипЗапасів.ActiveId = Елемент.ТипЗапасів.ToString();
                Валюта.Pointer = Елемент.Валюта;
                Контрагент.Pointer = Елемент.Контрагент;
                Договір.Pointer = Елемент.Договір;

    }

    protected override void GetValue()
    {
        Елемент.Код = Код.GetText();
                        Елемент.Назва = Назва.GetText();
                        Елемент.Організація = Організація.Pointer;
                Елемент.ТипЗапасів = ПсевдонімиПерелічення.ТипЗапасів_FindByName(ТипЗапасів.ActiveId);
                Елемент.Валюта = Валюта.Pointer;
                Елемент.Контрагент = Контрагент.Pointer;
                Елемент.Договір = Договір.Pointer;

    }

    #endregion

    protected override async ValueTask<bool> Save()
    {
        bool isSaved = false;
        try
        {
            if (await Елемент.Save())
            {

                isSaved = true;
            }
        }
        catch (Exception ex)
        {
            //ФункціїДляПовідомлень.ДодатиПовідомлення(Елемент.GetBasis(), Caption, ex);
        }
        return isSaved;
    }
}

/*     
        ВидиЗапасів_PointerTablePartCell.cs
        PointerTablePartCell
*/
using Gtk;
using InterfaceGtk4;
using GeneratedCode.Довідники;
using AccountingSoftware;

namespace StorageAndTrade;

public class ВидиЗапасів_PointerTablePartCell : PointerTablePartCell
{
    ВидиЗапасів_Pointer pointer = new();
    public ВидиЗапасів_Pointer Pointer
    {
        get => pointer;
        set
        {
            pointer = value;
            Presentation = pointer.Name;
        }
    }

    public async ValueTask GetPresentation() => Presentation = pointer != null ? await pointer.GetPresentation() : "";

    async ValueTask PointerChange(UnigueID? p)
    {
        Pointer = new ВидиЗапасів_Pointer(p ?? new UnigueID());
        await GetPresentation();
        OnSelect?.Invoke();
    }



    protected override async void Select(Button button, EventArgs args)
    {
        Popover popover = Popover.New();
        popover.SetParent(button);
        popover.WidthRequest = 800;
        popover.HeightRequest = 400;

        ВидиЗапасів_ШвидкийВибір page = new()
        {
            PopoverParent = popover,
            DirectoryPointerItem = pointer.UnigueID,
            CallBack_OnSelectPointer = async p => await PointerChange(p)
        };


        popover.SetChild(page);
        popover.Show();

        await page.SetValue();
    }

    protected override async void Clear()
    {
        await PointerChange(null);
    }
}

/*
        Банки_Функції.cs
        Функції
*/

using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Довідники;

namespace StorageAndTrade;

static class Банки_Функції
{
    public static List<Where> Відбори(string searchText)
    {
        return
        [

            //Код
            new Where(Банки_Const.Код, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //Назва
            new Where(Comparison.OR, Банки_Const.Назва, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //КодМФО
            new Where(Comparison.OR, Банки_Const.КодМФО, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //ПовнаНазва
            new Where(Comparison.OR, Банки_Const.ПовнаНазва, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //УнікальнийКодБанку
            new Where(Comparison.OR, Банки_Const.УнікальнийКодБанку, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //Адреса
            new Where(Comparison.OR, Банки_Const.Адреса, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //КодНБУ
            new Where(Comparison.OR, Банки_Const.КодНБУ, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //НомерЛіцензії
            new Where(Comparison.OR, Банки_Const.НомерЛіцензії, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

        ];
    }

    public static async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null, 
        Action<UnigueID?>? сallBack_LoadRecords = null, 
        Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        Банки_Елемент page = new()
        {
            CallBack_LoadRecords = сallBack_LoadRecords,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        if (IsNew)
        {
            await page.Елемент.New();

        }
        else if (unigueID == null || !await page.Елемент.Read(unigueID))
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return;
        }

        Program.BasicForm?.NotebookFunc.CreatePage(page.Caption, page);
        await page.SetValue();
    }

    public static async ValueTask OpenPageList(UnigueID? unigueID = null, Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        Банки_Список page = new()
        {
            DirectoryPointerItem = unigueID,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        Program.BasicForm?.NotebookFunc.CreatePage(Банки_Const.FULLNAME, page);
        await page.SetValue();
    }

    public static async ValueTask SetDeletionLabel(UnigueID unigueID)
    {
        Банки_Pointer Вказівник = new(unigueID);
        bool? label = await Вказівник.GetDeletionLabel();
        if (label.HasValue) await Вказівник.SetDeletionLabel(!label.Value);
    }

    public static async ValueTask<UnigueID?> Copy(UnigueID unigueID)
    {
        Банки_Objest Обєкт = new();
        if (await Обєкт.Read(unigueID))
        {
            Банки_Objest Новий = await Обєкт.Copy(true);
            await Новий.Save();

            return Новий.UnigueID;
        }
        else
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return null;
        }
    }
}

