/*
        Банки_Елемент.cs
        Елемент
*/

using Gtk;
using InterfaceGtk4;

using GeneratedCode.Довідники;
using GeneratedCode.Документи;
using GeneratedCode.Перелічення;

namespace StorageAndTrade;

class Банки_Елемент : DirectoryFormElement
{
    public Банки_Objest Елемент { get; init; } = new Банки_Objest();

    #region Fields
    Entry Код = new() { WidthRequest = 100 };
                    Entry Назва = new() { WidthRequest = 500 };
                    Entry ПовнаНазва = new() { WidthRequest = 500 };
                    Entry НазваГоловноїУстановиАнг = new() { WidthRequest = 500 };
                    Entry ТипНаселеногоПункту = new() { WidthRequest = 100 };
                    Entry КодМФО = new() { WidthRequest = 100 };
                    Entry КодЄДРПОУ = new() { WidthRequest = 100 };
                    Entry УнікальнийКодБанку = new() { WidthRequest = 100 };
                    Entry КодОбластіОпераційноїДіяльності = new() { WidthRequest = 100 };
                    Entry НазваОбластіОпераційноїДіяльності = new() { WidthRequest = 100 };
                    Entry КодОбластіЗгідноСтатуту = new() { WidthRequest = 100 };
                    Entry НазваОбластіЗгідноСтатуту = new() { WidthRequest = 100 };
                    Entry ПоштовийІндекс = new() { WidthRequest = 100 };
                    Entry НазваНаселеногоПункту = new() { WidthRequest = 100 };
                    Entry Адреса = new() { WidthRequest = 300 };
                    Entry КодТелефонногоЗвязку = new() { WidthRequest = 100 };
                    Entry НомерТелефону = new() { WidthRequest = 100 };
                    Entry ЧисловийКодСтануУстанови = new() { WidthRequest = 100 };
                    Entry НазваСтануУстанови = new() { WidthRequest = 300 };
                    Entry ДатаЗміниСтану = new() { WidthRequest = 100 };
                    Entry ДатаВідкриттяУстанови = new() { WidthRequest = 100 };
                    Entry ДатаЗакриттяУстанови = new() { WidthRequest = 100 };
                    Entry КодНБУ = new() { WidthRequest = 100 };
                    Entry НомерЛіцензії = new() { WidthRequest = 100 };
                    Entry ДатаЛіцензії = new() { WidthRequest = 100 };
                    Entry КодСтатусу = new() { WidthRequest = 100 };
                    Entry Статус = new() { WidthRequest = 300 };
                    Entry ДатаЗапису = new() { WidthRequest = 100 };

    #endregion

    #region TabularParts

    #endregion

    public Банки_Елемент() : base(Program.BasicForm?.NotebookFunc)
    { 
        Element = Елемент;

    }

    protected override void CreateStart(Box vBox)
    {

            // Код
            CreateField(vBox, "Код:", Код);

            // Назва
            CreateField(vBox, "Назва:", Назва);

            // ПовнаНазва
            CreateField(vBox, "Повна назва:", ПовнаНазва);

            // НазваГоловноїУстановиАнг
            CreateField(vBox, "Назва головної установи Анг:", НазваГоловноїУстановиАнг);

            // ТипНаселеногоПункту
            CreateField(vBox, "Тип населеного пункту:", ТипНаселеногоПункту);

            // КодМФО
            CreateField(vBox, "Код МФО:", КодМФО);

            // КодЄДРПОУ
            CreateField(vBox, "Код ЄДРПОУ:", КодЄДРПОУ);

            // УнікальнийКодБанку
            CreateField(vBox, "Унікальний код:", УнікальнийКодБанку);

            // КодОбластіОпераційноїДіяльності
            CreateField(vBox, "Код області операційної діяльності:", КодОбластіОпераційноїДіяльності);

            // НазваОбластіОпераційноїДіяльності
            CreateField(vBox, "Назва області операційної діяльності:", НазваОбластіОпераційноїДіяльності);

            // КодОбластіЗгідноСтатуту
            CreateField(vBox, "Код області згідно статуту:", КодОбластіЗгідноСтатуту);

            // НазваОбластіЗгідноСтатуту
            CreateField(vBox, "Назва області згідно статуту:", НазваОбластіЗгідноСтатуту);

            // ПоштовийІндекс
            CreateField(vBox, "Поштовий індекс:", ПоштовийІндекс);

            // НазваНаселеногоПункту
            CreateField(vBox, "Назва населеного пункту:", НазваНаселеногоПункту);

            // Адреса
            CreateField(vBox, "Адреса:", Адреса);

            // КодТелефонногоЗвязку
            CreateField(vBox, "Код телефонного зв'язку:", КодТелефонногоЗвязку);

            // НомерТелефону
            CreateField(vBox, "Номер телефону:", НомерТелефону);

            // ЧисловийКодСтануУстанови
            CreateField(vBox, "Числовий код стану установи:", ЧисловийКодСтануУстанови);

            // НазваСтануУстанови
            CreateField(vBox, "Назва стану установи:", НазваСтануУстанови);

            // ДатаЗміниСтану
            CreateField(vBox, "Дата зміни стану:", ДатаЗміниСтану);

            // ДатаВідкриттяУстанови
            CreateField(vBox, "Дата відкриття установи:", ДатаВідкриттяУстанови);

            // ДатаЗакриттяУстанови
            CreateField(vBox, "Дата закриття установи:", ДатаЗакриттяУстанови);

            // КодНБУ
            CreateField(vBox, "Код НБУ:", КодНБУ);

            // НомерЛіцензії
            CreateField(vBox, "Номер ліцензії:", НомерЛіцензії);

            // ДатаЛіцензії
            CreateField(vBox, "Дата ліцензії:", ДатаЛіцензії);

            // КодСтатусу
            CreateField(vBox, "Код статусу:", КодСтатусу);

            // Статус
            CreateField(vBox, "Статус:", Статус);

            // ДатаЗапису
            CreateField(vBox, "Дата запису:", ДатаЗапису);

    }

    protected override void CreateEnd(Box vBox)
    {

    }

    #region Присвоєння / зчитування значень

    public override async ValueTask AssignValue()
    {
        Код.SetText(Елемент.Код);
                        Назва.SetText(Елемент.Назва);
                        ПовнаНазва.SetText(Елемент.ПовнаНазва);
                        НазваГоловноїУстановиАнг.SetText(Елемент.НазваГоловноїУстановиАнг);
                        ТипНаселеногоПункту.SetText(Елемент.ТипНаселеногоПункту);
                        КодМФО.SetText(Елемент.КодМФО);
                        КодЄДРПОУ.SetText(Елемент.КодЄДРПОУ);
                        УнікальнийКодБанку.SetText(Елемент.УнікальнийКодБанку);
                        КодОбластіОпераційноїДіяльності.SetText(Елемент.КодОбластіОпераційноїДіяльності);
                        НазваОбластіОпераційноїДіяльності.SetText(Елемент.НазваОбластіОпераційноїДіяльності);
                        КодОбластіЗгідноСтатуту.SetText(Елемент.КодОбластіЗгідноСтатуту);
                        НазваОбластіЗгідноСтатуту.SetText(Елемент.НазваОбластіЗгідноСтатуту);
                        ПоштовийІндекс.SetText(Елемент.ПоштовийІндекс);
                        НазваНаселеногоПункту.SetText(Елемент.НазваНаселеногоПункту);
                        Адреса.SetText(Елемент.Адреса);
                        КодТелефонногоЗвязку.SetText(Елемент.КодТелефонногоЗвязку);
                        НомерТелефону.SetText(Елемент.НомерТелефону);
                        ЧисловийКодСтануУстанови.SetText(Елемент.ЧисловийКодСтануУстанови);
                        НазваСтануУстанови.SetText(Елемент.НазваСтануУстанови);
                        ДатаЗміниСтану.SetText(Елемент.ДатаЗміниСтану);
                        ДатаВідкриттяУстанови.SetText(Елемент.ДатаВідкриттяУстанови);
                        ДатаЗакриттяУстанови.SetText(Елемент.ДатаЗакриттяУстанови);
                        КодНБУ.SetText(Елемент.КодНБУ);
                        НомерЛіцензії.SetText(Елемент.НомерЛіцензії);
                        ДатаЛіцензії.SetText(Елемент.ДатаЛіцензії);
                        КодСтатусу.SetText(Елемент.КодСтатусу);
                        Статус.SetText(Елемент.Статус);
                        ДатаЗапису.SetText(Елемент.ДатаЗапису);

    }

    protected override void GetValue()
    {
        Елемент.Код = Код.GetText();
                        Елемент.Назва = Назва.GetText();
                        Елемент.ПовнаНазва = ПовнаНазва.GetText();
                        Елемент.НазваГоловноїУстановиАнг = НазваГоловноїУстановиАнг.GetText();
                        Елемент.ТипНаселеногоПункту = ТипНаселеногоПункту.GetText();
                        Елемент.КодМФО = КодМФО.GetText();
                        Елемент.КодЄДРПОУ = КодЄДРПОУ.GetText();
                        Елемент.УнікальнийКодБанку = УнікальнийКодБанку.GetText();
                        Елемент.КодОбластіОпераційноїДіяльності = КодОбластіОпераційноїДіяльності.GetText();
                        Елемент.НазваОбластіОпераційноїДіяльності = НазваОбластіОпераційноїДіяльності.GetText();
                        Елемент.КодОбластіЗгідноСтатуту = КодОбластіЗгідноСтатуту.GetText();
                        Елемент.НазваОбластіЗгідноСтатуту = НазваОбластіЗгідноСтатуту.GetText();
                        Елемент.ПоштовийІндекс = ПоштовийІндекс.GetText();
                        Елемент.НазваНаселеногоПункту = НазваНаселеногоПункту.GetText();
                        Елемент.Адреса = Адреса.GetText();
                        Елемент.КодТелефонногоЗвязку = КодТелефонногоЗвязку.GetText();
                        Елемент.НомерТелефону = НомерТелефону.GetText();
                        Елемент.ЧисловийКодСтануУстанови = ЧисловийКодСтануУстанови.GetText();
                        Елемент.НазваСтануУстанови = НазваСтануУстанови.GetText();
                        Елемент.ДатаЗміниСтану = ДатаЗміниСтану.GetText();
                        Елемент.ДатаВідкриттяУстанови = ДатаВідкриттяУстанови.GetText();
                        Елемент.ДатаЗакриттяУстанови = ДатаЗакриттяУстанови.GetText();
                        Елемент.КодНБУ = КодНБУ.GetText();
                        Елемент.НомерЛіцензії = НомерЛіцензії.GetText();
                        Елемент.ДатаЛіцензії = ДатаЛіцензії.GetText();
                        Елемент.КодСтатусу = КодСтатусу.GetText();
                        Елемент.Статус = Статус.GetText();
                        Елемент.ДатаЗапису = ДатаЗапису.GetText();

    }

    #endregion

    protected override async ValueTask<bool> Save()
    {
        bool isSaved = false;
        try
        {
            if (await Елемент.Save())
            {

                isSaved = true;
            }
        }
        catch (Exception ex)
        {
            //ФункціїДляПовідомлень.ДодатиПовідомлення(Елемент.GetBasis(), Caption, ex);
        }
        return isSaved;
    }
}

/*     
        Банки_PointerTablePartCell.cs
        PointerTablePartCell
*/
using Gtk;
using InterfaceGtk4;
using GeneratedCode.Довідники;
using AccountingSoftware;

namespace StorageAndTrade;

public class Банки_PointerTablePartCell : PointerTablePartCell
{
    Банки_Pointer pointer = new();
    public Банки_Pointer Pointer
    {
        get => pointer;
        set
        {
            pointer = value;
            Presentation = pointer.Name;
        }
    }

    public async ValueTask GetPresentation() => Presentation = pointer != null ? await pointer.GetPresentation() : "";

    async ValueTask PointerChange(UnigueID? p)
    {
        Pointer = new Банки_Pointer(p ?? new UnigueID());
        await GetPresentation();
        OnSelect?.Invoke();
    }



    protected override async void Select(Button button, EventArgs args)
    {
        Popover popover = Popover.New();
        popover.SetParent(button);
        popover.WidthRequest = 800;
        popover.HeightRequest = 400;

        Банки_ШвидкийВибір page = new()
        {
            PopoverParent = popover,
            DirectoryPointerItem = pointer.UnigueID,
            CallBack_OnSelectPointer = async p => await PointerChange(p)
        };


        popover.SetChild(page);
        popover.Show();

        await page.SetValue();
    }

    protected override async void Clear()
    {
        await PointerChange(null);
    }
}

/*
        СкладськіПриміщення_Функції.cs
        Функції
*/

using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Довідники;

namespace StorageAndTrade;

static class СкладськіПриміщення_Функції
{
    public static List<Where> Відбори(string searchText)
    {
        return
        [

            //Назва
            new Where(СкладськіПриміщення_Const.Назва, Comparison.LIKE, searchText) { FuncToField = "TO_CHAR", FuncToField_Param1 = "''" },

        ];
    }

    public static async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null, 
        Action<UnigueID?>? сallBack_LoadRecords = null, 
        Action<UnigueID>? сallBack_OnSelectPointer = null,
            Склади_Pointer? Власник = null)
    {
        СкладськіПриміщення_Елемент page = new()
        {
            CallBack_LoadRecords = сallBack_LoadRecords,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        if (IsNew)
        {
            await page.Елемент.New();

                if (Власник != null) page.ВласникДляНового = Власник;

        }
        else if (unigueID == null || !await page.Елемент.Read(unigueID))
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return;
        }

        Program.BasicForm?.NotebookFunc.CreatePage(page.Caption, page);
        await page.SetValue();
    }

    public static async ValueTask OpenPageList(UnigueID? unigueID = null, Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        СкладськіПриміщення_Список page = new()
        {
            DirectoryPointerItem = unigueID,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        Program.BasicForm?.NotebookFunc.CreatePage(СкладськіПриміщення_Const.FULLNAME, page);
        await page.SetValue();
    }

    public static async ValueTask SetDeletionLabel(UnigueID unigueID)
    {
        СкладськіПриміщення_Pointer Вказівник = new(unigueID);
        bool? label = await Вказівник.GetDeletionLabel();
        if (label.HasValue) await Вказівник.SetDeletionLabel(!label.Value);
    }

    public static async ValueTask<UnigueID?> Copy(UnigueID unigueID)
    {
        СкладськіПриміщення_Objest Обєкт = new();
        if (await Обєкт.Read(unigueID))
        {
            СкладськіПриміщення_Objest Новий = await Обєкт.Copy(true);
            await Новий.Save();

            return Новий.UnigueID;
        }
        else
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return null;
        }
    }
}

/*     
        СкладськіПриміщення.cs
        Список
*/

using Gtk;
using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Довідники;

using ТабличнийСписок = GeneratedCode.Довідники.ТабличніСписки.СкладськіПриміщення_Записи;
using Функції = StorageAndTrade.СкладськіПриміщення_Функції;

namespace StorageAndTrade;

class СкладськіПриміщення_Список : DirectoryFormJournalFull
{

    public Склади_PointerControl Власник = new Склади_PointerControl() { Caption = "Склад:" };


    public СкладськіПриміщення_Список() : base(Program.BasicForm?.NotebookFunc)
    {
        TypeName = СкладськіПриміщення_Const.POINTER;
        ТабличнийСписок.AddColumn(this);
        SetPagesSettings(50);
    }

    public override async ValueTask LoadRecords()
    {
        await ТабличнийСписок.LoadRecords(this);
    }

    public override async ValueTask UpdateRecords()
    {
        await ТабличнийСписок.UpdateRecords(this);
    }

    protected override async void SetSearch(string searchText)
    {
        WhereList = Функції.Відбори(searchText);
    }

    protected override void FillFilter(FilterControl filterControl)
    {
        ТабличнийСписок.CreateFilter(this);
    }

    protected override async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null)
    {
        await Функції.OpenPageElement(IsNew, unigueID, CallBack_LoadRecords, CallBack_OnSelectPointer);
    }

    protected override async ValueTask SetDeletionLabel(UnigueID unigueID)
    {
        await Функції.SetDeletionLabel(unigueID);
    }

    protected override async ValueTask<UnigueID?> Copy(UnigueID unigueID)
    {
        return await Функції.Copy(unigueID);
    }
}

