/*
        СкладськіПриміщення_Елемент.cs
        Елемент
*/

using Gtk;
using InterfaceGtk4;

using GeneratedCode.Довідники;
using GeneratedCode.Документи;
using GeneratedCode.Перелічення;

namespace StorageAndTrade;

class СкладськіПриміщення_Елемент : DirectoryFormElement
{
    public СкладськіПриміщення_Objest Елемент { get; init; } = new СкладськіПриміщення_Objest();

    public Склади_Pointer ВласникДляНового = new Склади_Pointer();

    #region Fields
    Entry Назва = new() { WidthRequest = 500 };
                    ComboBoxText НалаштуванняАдресногоЗберігання = new ComboBoxText();
            Склади_PointerControl Склад = new() { Caption = "Склад", WidthPresentation = 500 };

    #endregion

    #region TabularParts

    #endregion

    public СкладськіПриміщення_Елемент() : base(Program.BasicForm?.NotebookFunc)
    { 
        Element = Елемент;

        {
            //Заповнення списку
            foreach (var field in ПсевдонімиПерелічення.НалаштуванняАдресногоЗберігання_List())
                НалаштуванняАдресногоЗберігання.Append(field.Value.ToString(), field.Name);

            //Заборона прокрутки списку
            EventControllerScroll controller = EventControllerScroll.New(EventControllerScrollFlags.BothAxes);
            НалаштуванняАдресногоЗберігання.AddController(controller);
            controller.OnScroll += (_, _) => true;
        }

    }

    protected override void CreateStart(Box vBox)
    {

            // Назва
            CreateField(vBox, "Назва:", Назва);

            // НалаштуванняАдресногоЗберігання
            CreateField(vBox, "Адресне зберігання:", НалаштуванняАдресногоЗберігання);

            // Склад
            CreateField(vBox, null, Склад);

    }

    protected override void CreateEnd(Box vBox)
    {

    }

    #region Присвоєння / зчитування значень

    public override async ValueTask AssignValue()
    {

        if (IsNew) 
            Елемент.Склад = ВласникДляНового;
        Назва.SetText(Елемент.Назва);
                        НалаштуванняАдресногоЗберігання.ActiveId = Елемент.НалаштуванняАдресногоЗберігання.ToString();
                Склад.Pointer = Елемент.Склад;

    }

    protected override void GetValue()
    {
        Елемент.Назва = Назва.GetText();
                        Елемент.НалаштуванняАдресногоЗберігання = ПсевдонімиПерелічення.НалаштуванняАдресногоЗберігання_FindByName(НалаштуванняАдресногоЗберігання.ActiveId);
                Елемент.Склад = Склад.Pointer;

    }

    #endregion

    protected override async ValueTask<bool> Save()
    {
        bool isSaved = false;
        try
        {
            if (await Елемент.Save())
            {

                isSaved = true;
            }
        }
        catch (Exception ex)
        {
            //ФункціїДляПовідомлень.ДодатиПовідомлення(Елемент.GetBasis(), Caption, ex);
        }
        return isSaved;
    }
}

/*
        СкладськіКомірки_Функції.cs
        Функції
*/

using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Довідники;

namespace StorageAndTrade;

static class СкладськіКомірки_Функції
{
    public static List<Where> Відбори(string searchText)
    {
        return
        [

            //Назва
            new Where(СкладськіКомірки_Const.Назва, Comparison.LIKE, searchText) { FuncToField = "TO_CHAR", FuncToField_Param1 = "''" },

        ];
    }

    public static async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null, 
        Action<UnigueID?>? сallBack_LoadRecords = null, 
        Action<UnigueID>? сallBack_OnSelectPointer = null,
            СкладськіПриміщення_Pointer? Власник = null)
    {
        СкладськіКомірки_Елемент page = new()
        {
            CallBack_LoadRecords = сallBack_LoadRecords,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        if (IsNew)
        {
            await page.Елемент.New();

                if (Власник != null) page.ВласникДляНового = Власник;

        }
        else if (unigueID == null || !await page.Елемент.Read(unigueID))
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return;
        }

        Program.BasicForm?.NotebookFunc.CreatePage(page.Caption, page);
        await page.SetValue();
    }

    public static async ValueTask OpenPageList(UnigueID? unigueID = null, Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        СкладськіКомірки_Список page = new()
        {
            DirectoryPointerItem = unigueID,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        Program.BasicForm?.NotebookFunc.CreatePage(СкладськіКомірки_Const.FULLNAME, page);
        await page.SetValue();
    }

    public static async ValueTask SetDeletionLabel(UnigueID unigueID)
    {
        СкладськіКомірки_Pointer Вказівник = new(unigueID);
        bool? label = await Вказівник.GetDeletionLabel();
        if (label.HasValue) await Вказівник.SetDeletionLabel(!label.Value);
    }

    public static async ValueTask<UnigueID?> Copy(UnigueID unigueID)
    {
        СкладськіКомірки_Objest Обєкт = new();
        if (await Обєкт.Read(unigueID))
        {
            СкладськіКомірки_Objest Новий = await Обєкт.Copy(true);
            await Новий.Save();

            return Новий.UnigueID;
        }
        else
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return null;
        }
    }
}

