using Gtk;
using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Довідники;

using ТабличнийСписок = GeneratedCode.Довідники.ТабличніСписки.ТипорозміриКомірок_Записи;
using Функції = StorageAndTrade.ТипорозміриКомірок_Функції;

namespace StorageAndTrade;

class ТипорозміриКомірок_ШвидкийВибір : DirectoryFormJournalSmall
{


    public ТипорозміриКомірок_ШвидкийВибір() : base(Program.BasicForm?.NotebookFunc)
    {
        TypeName = ТипорозміриКомірок_Const.POINTER;
        KeyForSetting = ".Small";
        ТабличнийСписок.AddColumn(this);
        SetPagesSettings(50);
    }

    public override async ValueTask LoadRecords()
    {
        await ТабличнийСписок.LoadRecords(this);
    }

    public override async ValueTask UpdateRecords()
    {
        await ТабличнийСписок.UpdateRecords(this);
    }

    protected override async void SetSearch(string searchText)
    {
        WhereList = Функції.Відбори(searchText);
    }

    protected override void FillFilter(FilterControl filterControl)
    {
        ТабличнийСписок.CreateFilter(this);
    }

    protected override async ValueTask OpenPageList(UnigueID? unigueID = null)
    {
        await Функції.OpenPageList(unigueID, CallBack_OnSelectPointer);
    }

    protected override async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null)
    {
        await Функції.OpenPageElement(IsNew, unigueID, CallBack_LoadRecords, CallBack_OnSelectPointer);
    }

    protected override async ValueTask SetDeletionLabel(UnigueID unigueID)
    {
        await Функції.SetDeletionLabel(unigueID);
    }

    protected override async ValueTask<UnigueID?> Copy(UnigueID unigueID)
    {
        return await Функції.Copy(unigueID);
    }
}

/*     
        ТипорозміриКомірок_PointerControl.cs
        PointerControl
*/

using Gtk;
using InterfaceGtk4;
using GeneratedCode.Довідники;

namespace StorageAndTrade;

public class ТипорозміриКомірок_PointerControl : PointerControl
{
    event EventHandler<ТипорозміриКомірок_Pointer> PointerChanged;

    public ТипорозміриКомірок_PointerControl()
    {
        pointer = new ТипорозміриКомірок_Pointer();
        WidthPresentation = 300;
        Caption = $"{ТипорозміриКомірок_Const.FULLNAME}:";
        PointerChanged += async (_, pointer) => Presentation = pointer != null ? await pointer.GetPresentation() : "";
    }

    ТипорозміриКомірок_Pointer pointer;
    public ТипорозміриКомірок_Pointer Pointer
    {
        get
        {
            return pointer;
        }
        set
        {
            pointer = value;
            PointerChanged?.Invoke(null, pointer);
        }
    }



    protected override async void OpenSelect(Button button, EventArgs args)
    {
        Popover popover = Popover.New();
        popover.SetParent(button);
        popover.WidthRequest = 800;
        popover.HeightRequest = 400;
        BeforeClickOpenFunc?.Invoke();
        ТипорозміриКомірок_ШвидкийВибір page = new()
        {
            PopoverParent = popover,
            DirectoryPointerItem = Pointer.UnigueID,
            CallBack_OnSelectPointer = selectPointer =>
            {
                Pointer = new ТипорозміриКомірок_Pointer(selectPointer);
                AfterSelectFunc?.Invoke();
            }
        };

        popover.SetChild(page);
        popover.Show();

        await page.SetValue();
    }

    protected override void OnClear(Button button, EventArgs args)
    {
        Pointer = new ТипорозміриКомірок_Pointer();
        AfterSelectFunc?.Invoke();
        AfterClearFunc?.Invoke();
    }
}

/*
        ТипорозміриКомірок_Елемент.cs
        Елемент
*/

using Gtk;
using InterfaceGtk4;

using GeneratedCode.Довідники;
using GeneratedCode.Документи;
using GeneratedCode.Перелічення;

namespace StorageAndTrade;

class ТипорозміриКомірок_Елемент : DirectoryFormElement
{
    public ТипорозміриКомірок_Objest Елемент { get; init; } = new ТипорозміриКомірок_Objest();

    #region Fields
    Entry Назва = new() { WidthRequest = 250 };
                    Entry Висота = new() { WidthRequest = 100 };
                    Entry Ширина = new() { WidthRequest = 100 };
                    Entry Глибина = new() { WidthRequest = 100 };
                    Entry Обєм = new() { WidthRequest = 100 };
                    Entry Вантажопідйомність = new() { WidthRequest = 100 };

    #endregion

    #region TabularParts

    #endregion

    public ТипорозміриКомірок_Елемент() : base(Program.BasicForm?.NotebookFunc)
    { 
        Element = Елемент;

    }

    protected override void CreateStart(Box vBox)
    {

            // Назва
            CreateField(vBox, "Назва:", Назва);

            // Висота
            CreateField(vBox, "Висота:", Висота);

            // Ширина
            CreateField(vBox, "Ширина:", Ширина);

            // Глибина
            CreateField(vBox, "Глибина:", Глибина);

            // Обєм
            CreateField(vBox, "Обєм:", Обєм);

            // Вантажопідйомність
            CreateField(vBox, "Вантажопідйомність:", Вантажопідйомність);

    }

    protected override void CreateEnd(Box vBox)
    {

    }

    #region Присвоєння / зчитування значень

    public override async ValueTask AssignValue()
    {
        Назва.SetText(Елемент.Назва);
                        Висота.SetText(Елемент.Висота);
                        Ширина.SetText(Елемент.Ширина);
                        Глибина.SetText(Елемент.Глибина);
                        Обєм.SetText(Елемент.Обєм);
                        Вантажопідйомність.SetText(Елемент.Вантажопідйомність);

    }

    protected override void GetValue()
    {
        Елемент.Назва = Назва.GetText();
                        Елемент.Висота = Висота.GetText();
                        Елемент.Ширина = Ширина.GetText();
                        Елемент.Глибина = Глибина.GetText();
                        Елемент.Обєм = Обєм.GetText();
                        Елемент.Вантажопідйомність = Вантажопідйомність.GetText();

    }

    #endregion

    protected override async ValueTask<bool> Save()
    {
        bool isSaved = false;
        try
        {
            if (await Елемент.Save())
            {

                isSaved = true;
            }
        }
        catch (Exception ex)
        {
            //ФункціїДляПовідомлень.ДодатиПовідомлення(Елемент.GetBasis(), Caption, ex);
        }
        return isSaved;
    }
}

