            column.Resizable = true;
            column.Expand = true;
            Grid.AppendColumn(column);
        }
    }

    public override async ValueTask LoadRecords()
    {

        if (ЕлементВласник != null) 
        {

            ЕлементВласник.ЗвітСторінка_TablePart.FillJoin([]);
            await ЕлементВласник.ЗвітСторінка_TablePart.Read();


        Store.RemoveAll();


        foreach (ЗбереженіЗвіти_ЗвітСторінка_TablePart.Record record in ЕлементВласник.ЗвітСторінка_TablePart.Records)
        {
            Store.Append(new ItemRow()
            {
                UnigueID = new(record.UID),
                А = record.А,
                Б = record.Б,
                В = record.В,
                Г = record.Г,
                Ґ = record.Ґ,
                Д = record.Д,
                Е = record.Е,
                Є = record.Є,
                Ж = record.Ж,
                З = record.З,
                И = record.И,
                І = record.І,
                Ї = record.Ї,
                Й = record.Й,
                К = record.К,
                Л = record.Л,
                М = record.М,
                Н = record.Н,
                О = record.О,
                П = record.П,
                Р = record.Р,
                С = record.С,
                Т = record.Т,
                У = record.У,
                Ф = record.Ф,
                Х = record.Х,
                Ц = record.Ц,
                Ч = record.Ч,
                Ш = record.Ш,
                Щ = record.Щ,
                Ь = record.Ь,
                Ю = record.Ю,
                Я = record.Я,

            });

            if (SelectPosition > 0)
            {
                Grid.Model.SelectItem(SelectPosition, true);
                ScrollTo(SelectPosition);
            }
        }
        }
    }

    public override async ValueTask SaveRecords()
    {

        if (ЕлементВласник != null)
        {
        ЕлементВласник.ЗвітСторінка_TablePart.Records.Clear();
        for (uint i = 0; i <= Store.GetNItems(); i++)
        {
            ItemRow? row = (ItemRow?)Store.GetObject(i);
            if (row != null)
            {
                ЕлементВласник.ЗвітСторінка_TablePart.Records.Add(new()
                {
                    UID = row.UnigueID.UGuid,
                    А = row.А,
                    Б = row.Б,
                    В = row.В,
                    Г = row.Г,
                    Ґ = row.Ґ,
                    Д = row.Д,
                    Е = row.Е,
                    Є = row.Є,
                    Ж = row.Ж,
                    З = row.З,
                    И = row.И,
                    І = row.І,
                    Ї = row.Ї,
                    Й = row.Й,
                    К = row.К,
                    Л = row.Л,
                    М = row.М,
                    Н = row.Н,
                    О = row.О,
                    П = row.П,
                    Р = row.Р,
                    С = row.С,
                    Т = row.Т,
                    У = row.У,
                    Ф = row.Ф,
                    Х = row.Х,
                    Ц = row.Ц,
                    Ч = row.Ч,
                    Ш = row.Ш,
                    Щ = row.Щ,
                    Ь = row.Ь,
                    Ю = row.Ю,
                    Я = row.Я,

                });
            }
        }
        await ЕлементВласник.ЗвітСторінка_TablePart.Save(true);
        await LoadRecords();
        }
    }

    public override bool NewRecord()
    {
        Store.Append(new ItemRow());
        return true;
    }
}

/*
        ЗбереженіЗвіти_Функції.cs
        Функції
*/

using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Довідники;

namespace StorageAndTrade;

static class ЗбереженіЗвіти_Функції
{
    public static List<Where> Відбори(string searchText)
    {
        return
        [

            //Назва
            new Where(ЗбереженіЗвіти_Const.Назва, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //Опис
            new Where(Comparison.OR, ЗбереженіЗвіти_Const.Опис, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

        ];
    }

    public static async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null, 
        Action<UnigueID?>? сallBack_LoadRecords = null, 
        Action<UnigueID>? сallBack_OnSelectPointer = null,
            Користувачі_Pointer? Власник = null)
    {
        ЗбереженіЗвіти_Елемент page = new()
        {
            CallBack_LoadRecords = сallBack_LoadRecords,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        if (IsNew)
        {
            await page.Елемент.New();

                if (Власник != null) page.ВласникДляНового = Власник;

        }
        else if (unigueID == null || !await page.Елемент.Read(unigueID))
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return;
        }

        Program.BasicForm?.NotebookFunc.CreatePage(page.Caption, page);
        await page.SetValue();
    }

    public static async ValueTask OpenPageList(UnigueID? unigueID = null, Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        ЗбереженіЗвіти_Список page = new()
        {
            DirectoryPointerItem = unigueID,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        Program.BasicForm?.NotebookFunc.CreatePage(ЗбереженіЗвіти_Const.FULLNAME, page);
        await page.SetValue();
    }

    public static async ValueTask SetDeletionLabel(UnigueID unigueID)
    {
        ЗбереженіЗвіти_Pointer Вказівник = new(unigueID);
        bool? label = await Вказівник.GetDeletionLabel();
        if (label.HasValue) await Вказівник.SetDeletionLabel(!label.Value);
    }

    public static async ValueTask<UnigueID?> Copy(UnigueID unigueID)
    {
        ЗбереженіЗвіти_Objest Обєкт = new();
        if (await Обєкт.Read(unigueID))
        {
            ЗбереженіЗвіти_Objest Новий = await Обєкт.Copy(true);
            await Новий.Save();

                await Новий.ЗвітСторінка_TablePart.Save(false); // Таблична частина "ЗвітСторінка"

            return Новий.UnigueID;
        }
        else
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return null;
        }
    }
}

/*
        ЗбереженіЗвіти_Елемент.cs
        Елемент
*/

using Gtk;
using InterfaceGtk4;

using GeneratedCode.Довідники;
using GeneratedCode.Документи;
using GeneratedCode.Перелічення;

namespace StorageAndTrade;

class ЗбереженіЗвіти_Елемент : DirectoryFormElement
{
    public ЗбереженіЗвіти_Objest Елемент { get; init; } = new ЗбереженіЗвіти_Objest();

    public Користувачі_Pointer ВласникДляНового = new Користувачі_Pointer();

    #region Fields
    DateTimeControl Додано = new();
            Entry Назва = new() { WidthRequest = 500 };
                    TextView Опис = new TextView() { WrapMode = WrapMode.Word };

    #endregion

    #region TabularParts

        // Таблична частина "ЗвітСторінка"
        ЗбереженіЗвіти_ТабличнаЧастина_ЗвітСторінка ЗвітСторінка = new() { WidthRequest = 1500, HeightRequest = 600 };

    #endregion

    public ЗбереженіЗвіти_Елемент() : base(Program.BasicForm?.NotebookFunc)
    { 
        Element = Елемент;

    }

    protected override void CreateStart(Box vBox)
    {

            // Додано
            CreateField(vBox, "Додано:", Додано);

            // Назва
            CreateField(vBox, "Назва:", Назва);

            // Опис
            CreateFieldView(vBox, "Опис:", Опис, 1400, 100);

    }

    protected override void CreateEnd(Box vBox)
    {

            // Таблична частина "ЗвітСторінка" 
            CreateTablePart(vBox, "Звіт:", ЗвітСторінка);

    }

    #region Присвоєння / зчитування значень

    public override async ValueTask AssignValue()
    {

        if (IsNew) 
            Елемент.Користувач = ВласникДляНового;
        Додано.Value = Елемент.Додано;
                Назва.SetText(Елемент.Назва);
                        Опис.Buffer?.Text = Елемент.Опис;

            // Таблична частина "ЗвітСторінка"
            ЗвітСторінка.ЕлементВласник = Елемент;
            await ЗвітСторінка.LoadRecords();

    }

    protected override void GetValue()
    {
        Елемент.Додано = Додано.Value;
                Елемент.Назва = Назва.GetText();
                        Елемент.Опис = Опис.Buffer?.Text ?? "";

    }

    #endregion

    protected override async ValueTask<bool> Save()
    {
        bool isSaved = false;
        try
        {
            if (await Елемент.Save())
            {
                await ЗвітСторінка.SaveRecords(); // Таблична частина "ЗвітСторінка"

                isSaved = true;
            }
        }
        catch (Exception ex)
        {
            //ФункціїДляПовідомлень.ДодатиПовідомлення(Елемент.GetBasis(), Caption, ex);
        }
        return isSaved;
    }
}

