    public override async ValueTask LoadRecords()
    {

        if (ЕлементВласник != null) 
        {

            ЕлементВласник.Товари_TablePart.FillJoin([РеалізаціяТоварівТаПослуг_Товари_TablePart.НомерРядка,]);
            await ЕлементВласник.Товари_TablePart.Read();


        Store.RemoveAll();


        foreach (РеалізаціяТоварівТаПослуг_Товари_TablePart.Record record in ЕлементВласник.Товари_TablePart.Records)
        {
            Store.Append(new ItemRow()
            {
                UnigueID = new(record.UID),
                НомерРядка = record.НомерРядка,
                Номенклатура = record.Номенклатура,
                ХарактеристикаНоменклатури = record.ХарактеристикаНоменклатури,
                Серія = record.Серія,
                КількістьУпаковок = record.КількістьУпаковок,
                Пакування = record.Пакування,
                Кількість = record.Кількість,
                ВидЦіни = record.ВидЦіни,
                Ціна = record.Ціна,
                Сума = record.Сума,
                Скидка = record.Скидка,
                ЗамовленняКлієнта = record.ЗамовленняКлієнта,
                РахунокФактура = record.РахунокФактура,
                Склад = record.Склад,
                Партія = record.Партія,

            });

            if (SelectPosition > 0)
            {
                Grid.Model.SelectItem(SelectPosition, true);
                ScrollTo(SelectPosition);
            }
        }
        }
    }

    public override async ValueTask SaveRecords()
    {

        if (ЕлементВласник != null)
        {
        ЕлементВласник.Товари_TablePart.Records.Clear();
        for (uint i = 0; i <= Store.GetNItems(); i++)
        {
            ItemRow? row = (ItemRow?)Store.GetObject(i);
            if (row != null)
            {
                ЕлементВласник.Товари_TablePart.Records.Add(new()
                {
                    UID = row.UnigueID.UGuid,
                    НомерРядка = row.НомерРядка,
                    Номенклатура = row.Номенклатура,
                    ХарактеристикаНоменклатури = row.ХарактеристикаНоменклатури,
                    Серія = row.Серія,
                    КількістьУпаковок = row.КількістьУпаковок,
                    Пакування = row.Пакування,
                    Кількість = row.Кількість,
                    ВидЦіни = row.ВидЦіни,
                    Ціна = row.Ціна,
                    Сума = row.Сума,
                    Скидка = row.Скидка,
                    ЗамовленняКлієнта = row.ЗамовленняКлієнта,
                    РахунокФактура = row.РахунокФактура,
                    Склад = row.Склад,
                    Партія = row.Партія,

                });
            }
        }
        await ЕлементВласник.Товари_TablePart.Save(true);
        await LoadRecords();
        }
    }

    public override bool NewRecord()
    {
        Store.Append(new ItemRow());
        return true;
    }
}

/*
        РеалізаціяТоварівТаПослуг_Функції.cs
        Функції
*/

using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Документи;

namespace StorageAndTrade;

static class РеалізаціяТоварівТаПослуг_Функції
{
    public static List<Where> Відбори(string searchText)
    {
        return
        [

            //Назва
            new Where(РеалізаціяТоварівТаПослуг_Const.Назва, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //Коментар
            new Where(Comparison.OR, РеалізаціяТоварівТаПослуг_Const.Коментар, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

            //КлючовіСловаДляПошуку
            new Where(Comparison.OR, РеалізаціяТоварівТаПослуг_Const.КлючовіСловаДляПошуку, Comparison.LIKE, searchText) { FuncToField = "LOWER" },

        ];
    }

    public static async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null, 
        Action<UnigueID?>? сallBack_LoadRecords = null,
        Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        РеалізаціяТоварівТаПослуг_Елемент page = new()
        {
            CallBack_LoadRecords = сallBack_LoadRecords,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        if (IsNew)
            await page.Елемент.New();
        else if (unigueID == null || !await page.Елемент.Read(unigueID))
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return;
        }

        Program.BasicForm?.NotebookFunc.CreatePage(page.Caption, page);
        await page.SetValue();
    }

    public static async ValueTask OpenPageList(UnigueID? unigueID = null, Action<UnigueID>? сallBack_OnSelectPointer = null)
    {
        РеалізаціяТоварівТаПослуг_Список page = new()
        {
            DocumentPointerItem = unigueID,
            CallBack_OnSelectPointer = сallBack_OnSelectPointer
        };

        Program.BasicForm?.NotebookFunc.CreatePage(РеалізаціяТоварівТаПослуг_Const.FULLNAME, page);
        await page.SetValue();
    }

    public static async ValueTask SetDeletionLabel(UnigueID unigueID)
    {
        РеалізаціяТоварівТаПослуг_Pointer Вказівник = new(unigueID);
        bool? label = await Вказівник.GetDeletionLabel();
        if (label.HasValue) await Вказівник.SetDeletionLabel(!label.Value);
    }

    public static async ValueTask<UnigueID?> Copy(UnigueID unigueID)
    {
        РеалізаціяТоварівТаПослуг_Objest Обєкт = new();
        if (await Обєкт.Read(unigueID))
        {
            РеалізаціяТоварівТаПослуг_Objest Новий = await Обєкт.Copy(true);
            await Новий.Save();

                await Новий.Товари_TablePart.Save(false); // Таблична частина "Товари"

            return Новий.UnigueID;
        }
        else
        {
            Message.Error(Program.BasicApp, Program.BasicForm, "Не вдалось прочитати!");
            return null;
        }
    }
}

/*     
        РеалізаціяТоварівТаПослуг.cs
        Список
*/

using InterfaceGtk4;
using AccountingSoftware;
using GeneratedCode.Документи;

using ТабличнийСписок = GeneratedCode.Документи.ТабличніСписки.РеалізаціяТоварівТаПослуг_Записи;
using Функції = StorageAndTrade.РеалізаціяТоварівТаПослуг_Функції;

namespace StorageAndTrade;

public class РеалізаціяТоварівТаПослуг_Список : DocumentFormJournalFull
{
    public РеалізаціяТоварівТаПослуг_Список() : base(Program.BasicForm?.NotebookFunc)
    {
        TypeName = РеалізаціяТоварівТаПослуг_Const.POINTER;
        ТабличнийСписок.AddColumn(this);
        SetPagesSettings(50, Pages.StartingPosition.End);
    }

    #region Override

    public override async ValueTask LoadRecords()
    {
        await ТабличнийСписок.LoadRecords(this);
    }

    public override async ValueTask UpdateRecords()
    {
        await ТабличнийСписок.UpdateRecords(this);
    }

    protected override async void SetSearch(string searchText)
    {
        WhereList = Функції.Відбори(searchText);
    }

    protected override void FillFilter(FilterControl filterControl)
    {
        ТабличнийСписок.CreateFilter(this);
    }

    protected override async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null)
    {
        await Функції.OpenPageElement(IsNew, unigueID, CallBack_LoadRecords, CallBack_OnSelectPointer);
    }

    protected override async ValueTask SetDeletionLabel(UnigueID unigueID)
    {
        await Функції.SetDeletionLabel(unigueID);
    }

    protected override async ValueTask<UnigueID?> Copy(UnigueID unigueID)
    {
        return await Функції.Copy(unigueID);
    }

    protected override async ValueTask BeforeSetValue()
    {
        await ФункціїНалаштуванняКористувача.ОтриматиПеріодДляЖурналу(FormKey, Period);
    }

    protected override async void PeriodChanged()
    {
        ФункціїНалаштуванняКористувача.ЗаписатиПеріодДляЖурналу(FormKey, Period.Period.ToString(), Period.DateStart, Period.DateStop);
    }

    protected override async ValueTask SpendTheDocument(UnigueID[] unigueID, bool spendDoc)
    {

    }

    protected override void ReportSpendTheDocument(UnigueID[] unigueID)
    {

    }

    protected override async ValueTask VersionsHistory(UnigueID[] unigueID)
    {

    }

    #endregion
}

