/*     
        РахунокФактура_PointerControl.cs
        PointerControl
*/
using Gtk;
using InterfaceGtk4;
using GeneratedCode.Документи;

namespace StorageAndTrade;

public class РахунокФактура_PointerControl : PointerControl
{
    event EventHandler<РахунокФактура_Pointer> PointerChanged;

    public РахунокФактура_PointerControl()
    {
        pointer = new РахунокФактура_Pointer();
        WidthPresentation = 300;
        Caption = $"{РахунокФактура_Const.FULLNAME}:";
        PointerChanged += async (_, pointer) => Presentation = pointer != null ? await pointer.GetPresentation() : "";
    }

    РахунокФактура_Pointer pointer;
    public РахунокФактура_Pointer Pointer
    {
        get
        {
            return pointer;
        }
        set
        {
            pointer = value;
            PointerChanged?.Invoke(null, pointer);
        }
    }

    protected override async void OpenSelect(Button button, EventArgs args)
    {
        Popover popover = Popover.New();
        popover.SetParent(button);
        popover.WidthRequest = 800;
        popover.HeightRequest = 400;
        BeforeClickOpenFunc?.Invoke();
        РахунокФактура_ШвидкийВибір page = new()
        {
            PopoverParent = popover,
            DocumentPointerItem = Pointer.UnigueID,
            CallBack_OnSelectPointer = selectPointer =>
            {
                Pointer = new РахунокФактура_Pointer(selectPointer);
                AfterSelectFunc?.Invoke();
            }
        };
        popover.SetChild(page);
        popover.Show();

        await page.SetValue();
    }

    protected override void OnClear(Button button, EventArgs args)
    {
        Pointer = new РахунокФактура_Pointer();
        AfterSelectFunc?.Invoke();
        AfterClearFunc?.Invoke();
    }
}

/*     
        РахунокФактура_PointerTablePartCell.cs
        PointerTablePartCell
*/
using Gtk;
using InterfaceGtk4;
using GeneratedCode.Документи;
using AccountingSoftware;

namespace StorageAndTrade;

public class РахунокФактура_PointerTablePartCell : PointerTablePartCell
{
    РахунокФактура_Pointer pointer = new();
    public РахунокФактура_Pointer Pointer
    {
        get => pointer;
        set
        {
            pointer = value;
            Presentation = pointer.Name;
        }
    }

    public async ValueTask GetPresentation() => Presentation = pointer != null ? await pointer.GetPresentation() : "";

    async ValueTask PointerChange(UnigueID? p)
    {
        Pointer = new РахунокФактура_Pointer(p ?? new UnigueID());
        await GetPresentation();
        OnSelect?.Invoke();
    }

    protected override async void Select(Button button, EventArgs args)
    {
        Popover popover = Popover.New();
        popover.SetParent(button);
        popover.WidthRequest = 800;
        popover.HeightRequest = 400;

        РахунокФактура_ШвидкийВибір page = new()
        {
            PopoverParent = popover,
            DocumentPointerItem = pointer.UnigueID,
            CallBack_OnSelectPointer = async p => await PointerChange(p)
        };

        popover.SetChild(page);
        popover.Show();

        await page.SetValue();
    }

    protected override async void Clear()
    {
        await PointerChange(null);
    }
}

/*
        ЧекККМ_ТабличнаЧастина_Товари.cs
        Таблична Частина
*/

using Gtk;
using InterfaceGtk;
using AccountingSoftware;
using GeneratedCode.Довідники;
using GeneratedCode.Документи;
using GeneratedCode.Перелічення;


namespace StorageAndTrade
{
    class ЧекККМ_ТабличнаЧастина_Товари : ДокументТабличнаЧастина
    {

        public ЧекККМ_Objest? ЕлементВласник { get; set; }


        #region Записи

        enum Columns
        {
            НомерРядка,
            Номенклатура,
            ХарактеристикаНоменклатури,
            Серія,
            КількістьУпаковок,
            Пакування,
            Кількість,
            ВидЦіни,
            Ціна,
            СумаБезЗнижки,
            Сума,
            Знижка,

        }

        ListStore Store = new ListStore([

            typeof(int), //НомерРядка
            typeof(string), //Номенклатура
            typeof(string), //ХарактеристикаНоменклатури
            typeof(string), //Серія
            typeof(int), //КількістьУпаковок
            typeof(string), //Пакування
            typeof(float), //Кількість
            typeof(string), //ВидЦіни
            typeof(float), //Ціна
            typeof(float), //СумаБезЗнижки
            typeof(float), //Сума
            typeof(float), //Знижка
        ]);

        List<Запис> Записи = [];

        private class Запис
        {
            public Guid ID { get; set; } = Guid.Empty;
            public int НомерРядка { get; set; } = 0;
            public Номенклатура_Pointer Номенклатура { get; set; } = new Номенклатура_Pointer();
            public ХарактеристикиНоменклатури_Pointer ХарактеристикаНоменклатури { get; set; } = new ХарактеристикиНоменклатури_Pointer();
            public СеріїНоменклатури_Pointer Серія { get; set; } = new СеріїНоменклатури_Pointer();
            public int КількістьУпаковок { get; set; } = 0;
            public ПакуванняОдиниціВиміру_Pointer Пакування { get; set; } = new ПакуванняОдиниціВиміру_Pointer();
            public decimal Кількість { get; set; } = 0;
            public ВидиЦін_Pointer ВидЦіни { get; set; } = new ВидиЦін_Pointer();
            public decimal Ціна { get; set; } = 0;
            public decimal СумаБезЗнижки { get; set; } = 0;
            public decimal Сума { get; set; } = 0;
            public decimal Знижка { get; set; } = 0;


            public object[] ToArray()
            {
                return
                [   
                    НомерРядка,
                    Номенклатура.Назва,
                    ХарактеристикаНоменклатури.Назва,
                    Серія.Назва,
                    КількістьУпаковок,
                    Пакування.Назва,
                    (float)Кількість,
                    ВидЦіни.Назва,
                    (float)Ціна,
                    (float)СумаБезЗнижки,
                    (float)Сума,
                    (float)Знижка,

                ];
            }

            public static Запис Clone(Запис запис)
            {
                return new Запис
                {
                    ID = Guid.Empty,
                    НомерРядка = запис.НомерРядка,
                    Номенклатура = запис.Номенклатура.Copy(),
                    ХарактеристикаНоменклатури = запис.ХарактеристикаНоменклатури.Copy(),
                    Серія = запис.Серія.Copy(),
                    КількістьУпаковок = запис.КількістьУпаковок,
                    Пакування = запис.Пакування.Copy(),
                    Кількість = запис.Кількість,
                    ВидЦіни = запис.ВидЦіни.Copy(),
                    Ціна = запис.Ціна,
                    СумаБезЗнижки = запис.СумаБезЗнижки,
                    Сума = запис.Сума,
                    Знижка = запис.Знижка,

                };
            }

            public static async ValueTask ПісляЗміни_Номенклатура(Запис запис)
            {
                await запис.Номенклатура.GetPresentation();
            }

            public static async ValueTask ПісляЗміни_ХарактеристикаНоменклатури(Запис запис)
            {
                await запис.ХарактеристикаНоменклатури.GetPresentation();
            }

            public static async ValueTask ПісляЗміни_Серія(Запис запис)
            {
                await запис.Серія.GetPresentation();
            }

            public static async ValueTask ПісляЗміни_Пакування(Запис запис)
            {
                await запис.Пакування.GetPresentation();
            }

            public static async ValueTask ПісляЗміни_ВидЦіни(Запис запис)
            {
                await запис.ВидЦіни.GetPresentation();
            }

        }

        #endregion

        public ЧекККМ_ТабличнаЧастина_Товари()
        {
            TreeViewGrid.Model = Store;
            AddColumn();
        }

        void AddColumn()
        {

            //НомерРядка
            {

                TreeViewColumn column = new TreeViewColumn("№", new CellRendererText() { Xalign = 1 }, "text", (int)Columns.НомерРядка) { Alignment = 1, MinWidth = 30 };

                SetColIndex(column, Columns.НомерРядка);
                TreeViewGrid.AppendColumn(column);
            }

            //Номенклатура
            {

                TreeViewColumn column = new TreeViewColumn("Номенклатура", new CellRendererText(), "text", (int)Columns.Номенклатура) { Resizable = true, MinWidth = 200 };

                SetColIndex(column, Columns.Номенклатура);
                TreeViewGrid.AppendColumn(column);
            }

            //ХарактеристикаНоменклатури
            {

                TreeViewColumn column = new TreeViewColumn("Характеристика", new CellRendererText(), "text", (int)Columns.ХарактеристикаНоменклатури) { Resizable = true, MinWidth = 200 };

                SetColIndex(column, Columns.ХарактеристикаНоменклатури);
                TreeViewGrid.AppendColumn(column);
            }

            //Серія
            {

                TreeViewColumn column = new TreeViewColumn("Серія", new CellRendererText(), "text", (int)Columns.Серія) { Resizable = true, MinWidth = 200 };

                SetColIndex(column, Columns.Серія);
                TreeViewGrid.AppendColumn(column);
            }

            //КількістьУпаковок
            {

                CellRendererText cellNumber = new CellRendererText() { Editable = true, Xalign = 1 };
                cellNumber.Edited += EditCell;
                cellNumber.EditingStarted += EditingStarted;
                TreeViewColumn column = new TreeViewColumn("Коєфіціент", cellNumber, "text", (int)Columns.КількістьУпаковок) { Resizable = true, Alignment = 1, MinWidth = 100 };
                column.SetCellDataFunc(cellNumber, new TreeCellDataFunc(NumericCellDataFunc));

                SetColIndex(column, Columns.КількістьУпаковок);
                TreeViewGrid.AppendColumn(column);
            }

            //Пакування
            {

                TreeViewColumn column = new TreeViewColumn("Пакування", new CellRendererText(), "text", (int)Columns.Пакування) { Resizable = true, MinWidth = 200 };

                SetColIndex(column, Columns.Пакування);
                TreeViewGrid.AppendColumn(column);
            }

            //Кількість
            {

                CellRendererText cellNumber = new CellRendererText() { Editable = true, Xalign = 1 };
                cellNumber.Edited += EditCell;
                cellNumber.EditingStarted += EditingStarted;
                TreeViewColumn column = new TreeViewColumn("Кількість", cellNumber, "text", (int)Columns.Кількість) { Resizable = true, Alignment = 1, MinWidth = 100 };
                column.SetCellDataFunc(cellNumber, new TreeCellDataFunc(NumericCellDataFunc));

                SetColIndex(column, Columns.Кількість);
                TreeViewGrid.AppendColumn(column);
            }

            //ВидЦіни
            {

                TreeViewColumn column = new TreeViewColumn("Вид ціни", new CellRendererText(), "text", (int)Columns.ВидЦіни) { Resizable = true, MinWidth = 200 };

                SetColIndex(column, Columns.ВидЦіни);
                TreeViewGrid.AppendColumn(column);
            }

            //Ціна
            {

                CellRendererText cellNumber = new CellRendererText() { Editable = true, Xalign = 1 };
                cellNumber.Edited += EditCell;
                cellNumber.EditingStarted += EditingStarted;
                TreeViewColumn column = new TreeViewColumn("Ціна", cellNumber, "text", (int)Columns.Ціна) { Resizable = true, Alignment = 1, MinWidth = 100 };
                column.SetCellDataFunc(cellNumber, new TreeCellDataFunc(NumericCellDataFunc));

                SetColIndex(column, Columns.Ціна);
                TreeViewGrid.AppendColumn(column);
            }

            //СумаБезЗнижки
            {

                CellRendererText cellNumber = new CellRendererText() { Editable = true, Xalign = 1 };
                cellNumber.Edited += EditCell;
                cellNumber.EditingStarted += EditingStarted;
                TreeViewColumn column = new TreeViewColumn("Сума без знижки", cellNumber, "text", (int)Columns.СумаБезЗнижки) { Resizable = true, Alignment = 1, MinWidth = 100 };
                column.SetCellDataFunc(cellNumber, new TreeCellDataFunc(NumericCellDataFunc));

                SetColIndex(column, Columns.СумаБезЗнижки);
                TreeViewGrid.AppendColumn(column);
            }

            //Сума
            {

                CellRendererText cellNumber = new CellRendererText() { Editable = true, Xalign = 1 };
                cellNumber.Edited += EditCell;
                cellNumber.EditingStarted += EditingStarted;
                TreeViewColumn column = new TreeViewColumn("Сума", cellNumber, "text", (int)Columns.Сума) { Resizable = true, Alignment = 1, MinWidth = 100 };
                column.SetCellDataFunc(cellNumber, new TreeCellDataFunc(NumericCellDataFunc));

                SetColIndex(column, Columns.Сума);
                TreeViewGrid.AppendColumn(column);
            }

            //Знижка
            {

                CellRendererText cellNumber = new CellRendererText() { Editable = true, Xalign = 1 };
                cellNumber.Edited += EditCell;
                cellNumber.EditingStarted += EditingStarted;
                TreeViewColumn column = new TreeViewColumn("Знижка", cellNumber, "text", (int)Columns.Знижка) { Resizable = true, Alignment = 1, MinWidth = 100 };
                column.SetCellDataFunc(cellNumber, new TreeCellDataFunc(NumericCellDataFunc));

                SetColIndex(column, Columns.Знижка);
                TreeViewGrid.AppendColumn(column);
            }

            //Колонка пустишка для заповнення вільного простору
            TreeViewGrid.AppendColumn(new TreeViewColumn());            
        }
        #region Load and Save

        public override async ValueTask LoadRecords()
        {

            if (ЕлементВласник != null) 
            {

                ЕлементВласник.Товари_TablePart.FillJoin([ЧекККМ_Товари_TablePart.НомерРядка,]);
                await ЕлементВласник.Товари_TablePart.Read();


            Записи.Clear();
            Store.Clear();


            foreach (ЧекККМ_Товари_TablePart.Record record in ЕлементВласник.Товари_TablePart.Records)
            {
                Запис запис = new Запис
                {
                    ID = record.UID,
                    НомерРядка = record.НомерРядка,
                    Номенклатура = record.Номенклатура,
                    ХарактеристикаНоменклатури = record.ХарактеристикаНоменклатури,
                    Серія = record.Серія,
                    КількістьУпаковок = record.КількістьУпаковок,
                    Пакування = record.Пакування,
                    Кількість = record.Кількість,
                    ВидЦіни = record.ВидЦіни,
                    Ціна = record.Ціна,
                    СумаБезЗнижки = record.СумаБезЗнижки,
                    Сума = record.Сума,
                    Знижка = record.Знижка,

                };

                Записи.Add(запис);
                Store.AppendValues(запис.ToArray());
            }
            }
            SelectRowActivated();
        }

        public override async ValueTask SaveRecords()
        {

            if (ЕлементВласник != null)
            {
            ЕлементВласник.Товари_TablePart.Records.Clear();
            foreach (Запис запис in Записи)
            {
                ЕлементВласник.Товари_TablePart.Records.Add(new ЧекККМ_Товари_TablePart.Record()
                {
                    UID = запис.ID,
                    НомерРядка = запис.НомерРядка,
                    Номенклатура = запис.Номенклатура,
                    ХарактеристикаНоменклатури = запис.ХарактеристикаНоменклатури,
                    Серія = запис.Серія,
                    КількістьУпаковок = запис.КількістьУпаковок,
                    Пакування = запис.Пакування,
                    Кількість = запис.Кількість,
                    ВидЦіни = запис.ВидЦіни,
                    Ціна = запис.Ціна,
                    СумаБезЗнижки = запис.СумаБезЗнижки,
                    Сума = запис.Сума,
                    Знижка = запис.Знижка,

                });
            }
            await ЕлементВласник.Товари_TablePart.Save(true);
            await LoadRecords();
            }
        }

        /*public string КлючовіСловаДляПошуку()
        {

            string keyWords = "";
            foreach (Запис запис in Записи)
                keyWords += $"\n {запис.Номенклатура.Назва} {запис.ХарактеристикаНоменклатури.Назва} {запис.Серія.Назва} {запис.Пакування.Назва} {запис.ВидЦіни.Назва}";

            return keyWords;
        }*/

        #endregion

        #region Func


        protected override ФормаЖурнал? OpenSelect(TreeIter iter, int rowNumber, int colNumber)
        {
            Запис запис = Записи[rowNumber];
            switch ((Columns)colNumber)
            {
                case Columns.Номенклатура:
                {
                    Номенклатура_ШвидкийВибір page = new()
                    {
                        DirectoryPointerItem = запис.Номенклатура.UnigueID,
                        CallBack_OnSelectPointer = async (UnigueID selectPointer) =>
                        {
                            запис.Номенклатура = new Номенклатура_Pointer(selectPointer);
                            await Запис.ПісляЗміни_Номенклатура(запис);
                            Store.SetValues(iter, запис.ToArray());
                        }
                    };
                    return page;
                }
                case Columns.ХарактеристикаНоменклатури:
                {
                    ХарактеристикиНоменклатури_ШвидкийВибір page = new()
                    {
                        DirectoryPointerItem = запис.ХарактеристикаНоменклатури.UnigueID,
                        CallBack_OnSelectPointer = async (UnigueID selectPointer) =>
                        {
                            запис.ХарактеристикаНоменклатури = new ХарактеристикиНоменклатури_Pointer(selectPointer);
                            await Запис.ПісляЗміни_ХарактеристикаНоменклатури(запис);
                            Store.SetValues(iter, запис.ToArray());
                        }
                    };
                    return page;
                }
                case Columns.Серія:
                {
                    СеріїНоменклатури_ШвидкийВибір page = new()
                    {
                        DirectoryPointerItem = запис.Серія.UnigueID,
                        CallBack_OnSelectPointer = async (UnigueID selectPointer) =>
                        {
                            запис.Серія = new СеріїНоменклатури_Pointer(selectPointer);
                            await Запис.ПісляЗміни_Серія(запис);
                            Store.SetValues(iter, запис.ToArray());
                        }
                    };
                    return page;
                }
                case Columns.Пакування:
                {
                    ПакуванняОдиниціВиміру_ШвидкийВибір page = new()
                    {
                        DirectoryPointerItem = запис.Пакування.UnigueID,
                        CallBack_OnSelectPointer = async (UnigueID selectPointer) =>
                        {
                            запис.Пакування = new ПакуванняОдиниціВиміру_Pointer(selectPointer);
                            await Запис.ПісляЗміни_Пакування(запис);
                            Store.SetValues(iter, запис.ToArray());
                        }
                    };
                    return page;
                }
                case Columns.ВидЦіни:
                {
                    ВидиЦін_ШвидкийВибір page = new()
                    {
                        DirectoryPointerItem = запис.ВидЦіни.UnigueID,
                        CallBack_OnSelectPointer = async (UnigueID selectPointer) =>
                        {
                            запис.ВидЦіни = new ВидиЦін_Pointer(selectPointer);
                            await Запис.ПісляЗміни_ВидЦіни(запис);
                            Store.SetValues(iter, запис.ToArray());
                        }
                    };
                    return page;
                }

                default: return null;
            }
        }

        protected override void ClearCell(TreeIter iter, int rowNumber, int colNumber)
        {
            Запис запис = Записи[rowNumber];
            switch ((Columns)colNumber)
            {
                case Columns.Номенклатура: { запис.Номенклатура.Clear(); break; }
                case Columns.ХарактеристикаНоменклатури: { запис.ХарактеристикаНоменклатури.Clear(); break; }
                case Columns.Серія: { запис.Серія.Clear(); break; }
                case Columns.Пакування: { запис.Пакування.Clear(); break; }
                case Columns.ВидЦіни: { запис.ВидЦіни.Clear(); break; }

                default: break;             
            }
            Store.SetValues(iter, запис.ToArray());
        }

        protected override void ChangeCell(TreeIter iter, int rowNumber, int colNumber, string newText)
        {
            Запис запис = Записи[rowNumber];
            switch ((Columns)colNumber)
            {
                case Columns.КількістьУпаковок: { var (check, value) = Validate.IsInt(newText); if (check) запис.КількістьУпаковок = value; break; }
                case Columns.Кількість: { var (check, value) = Validate.IsDecimal(newText); if (check) запис.Кількість = value; break; }
                case Columns.Ціна: { var (check, value) = Validate.IsDecimal(newText); if (check) запис.Ціна = value; break; }
                case Columns.СумаБезЗнижки: { var (check, value) = Validate.IsDecimal(newText); if (check) запис.СумаБезЗнижки = value; break; }
                case Columns.Сума: { var (check, value) = Validate.IsDecimal(newText); if (check) запис.Сума = value; break; }
                case Columns.Знижка: { var (check, value) = Validate.IsDecimal(newText); if (check) запис.Знижка = value; break; }

                default: break;
            }
            Store.SetValues(iter, запис.ToArray());
        }

        void NumericCellDataFunc(TreeViewColumn column, CellRenderer cell, ITreeModel model, TreeIter iter)
        {
            if (GetColIndex(column, out int colNumber))
            {
                int rowNumber = int.Parse(Store.GetPath(iter).ToString());
                if (rowNumber >= 0 && rowNumber < Записи.Count)
                {
                    Запис запис = Записи[rowNumber];

                    CellRendererText cellText = (CellRendererText)cell;
                    cellText.Foreground = "green";

                    switch ((Columns)colNumber)
                    {

                        case Columns.КількістьУпаковок:
                        {
                            cellText.Text = запис.КількістьУпаковок.ToString();
                            break;
                        }

                        case Columns.Кількість:
                        {
                            cellText.Text = запис.Кількість.ToString();
                            break;
                        }

                        case Columns.Ціна:
                        {
                            cellText.Text = запис.Ціна.ToString();
                            break;
                        }

                        case Columns.СумаБезЗнижки:
                        {
                            cellText.Text = запис.СумаБезЗнижки.ToString();
                            break;
                        }

                        case Columns.Сума:
                        {
                            cellText.Text = запис.Сума.ToString();
                            break;
                        }

                        case Columns.Знижка:
                        {
                            cellText.Text = запис.Знижка.ToString();
                            break;
                        }

                        default: break;
                    }
                }
            }
        }


        #endregion

        #region ToolBar

        (Запис запис, TreeIter iter) НовийЗапис()
        {
            Запис запис = new Запис();
            Записи.Add(запис);

            TreeIter iter = Store.AppendValues(запис.ToArray());
            TreeViewGrid.SetCursor(Store.GetPath(iter), TreeViewGrid.Columns[0], false);

            return (запис, iter);
        }

        protected override void AddRecord()
        {
            НовийЗапис();
        }

        protected override void CopyRecord(int rowNumber)
        {
            Запис запис = Записи[rowNumber];
            Запис записНовий = Запис.Clone(запис);
            Записи.Add(записНовий);

            TreeIter iter = Store.AppendValues(записНовий.ToArray());
            TreeViewGrid.SetCursor(Store.GetPath(iter), TreeViewGrid.Columns[0], false);
        }

        protected override void DeleteRecord(TreeIter iter, int rowNumber)
        {
            Запис запис = Записи[rowNumber];
            Записи.Remove(запис);
            Store.Remove(ref iter);
        }

        #endregion
    }
}

/*
        ЧекККМ_SpendTheDocument.cs
        Модуль проведення документу
*/

using AccountingSoftware;
using GeneratedCode;

using GeneratedCode.Довідники;
using GeneratedCode.РегістриНакопичення;
using GeneratedCode.РегістриВідомостей;

namespace GeneratedCode.Документи
{
    static class ЧекККМ_SpendTheDocument
    {
        public static async ValueTask<bool> Spend(ЧекККМ_Objest ДокументОбєкт)
        {
            try
            {
                // Проведення документу
                // ...

                return true;
            }
            catch (Exception ex)
            {
                await СпільніФункції.ДокументНеПроводиться(ДокументОбєкт, ДокументОбєкт.Назва, ex.Message);
                return false;
            }
        }

        public static async ValueTask Clear(ЧекККМ_Objest ДокументОбєкт)
        {
            await ValueTask.FromResult(true);
        }
    }
}

/*
        ЧекККМ_Triggers.cs
        Тригери
*/

using GeneratedCode.Константи;
using AccountingSoftware;

namespace GeneratedCode.Документи
{
    static class ЧекККМ_Triggers
    {
        public static async ValueTask New(ЧекККМ_Objest ДокументОбєкт)
        {
            ДокументОбєкт.НомерДок = (++НумераціяДокументів.ЧекККМ_Const).ToString("D8");
            ДокументОбєкт.ДатаДок = DateTime.Now;
            await ValueTask.FromResult(true);
        }

        public static async ValueTask Copying(ЧекККМ_Objest ДокументОбєкт, ЧекККМ_Objest Основа)
        {

            ДокументОбєкт.Назва += " - Копія";

            await ValueTask.FromResult(true);
        }

        public static async ValueTask BeforeSave(ЧекККМ_Objest ДокументОбєкт)
        {
            ДокументОбєкт.Назва = $"{ЧекККМ_Const.FULLNAME} №{ДокументОбєкт.НомерДок} від {ДокументОбєкт.ДатаДок.ToString("dd.MM.yyyy")}";
            await ValueTask.FromResult(true);
        }

        public static async ValueTask AfterSave(ЧекККМ_Objest ДокументОбєкт)
        {
            await ValueTask.FromResult(true);
        }

        public static async ValueTask SetDeletionLabel(ЧекККМ_Objest ДокументОбєкт, bool label)
        {
            await ValueTask.FromResult(true);
        }

        public static async ValueTask BeforeDelete(ЧекККМ_Objest ДокументОбєкт)
        {
            await ValueTask.FromResult(true);
        }
    }
}

/*
        ЧекККМ_Функції.cs
        Функції
*/

using InterfaceGtk;
using AccountingSoftware;
using GeneratedCode.Документи;

namespace StorageAndTrade
{
    static class ЧекККМ_Функції
    {
        public static List<Where> Відбори(string searchText)
        {
            return
            [

                //Назва
                new Where(ЧекККМ_Const.Назва, Comparison.LIKE, searchText) { FuncToField = "TO_CHAR", FuncToField_Param1 = "''" },

            ];
        }

        public static async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null, 
            Action<UnigueID?>? сallBack_LoadRecords = null)
        {
            ЧекККМ_Елемент page = new ЧекККМ_Елемент
            {
                IsNew = IsNew,
                CallBack_LoadRecords = сallBack_LoadRecords
            };

            if (IsNew)
                await page.Елемент.New();
            else if (unigueID == null || !await page.Елемент.Read(unigueID))
            {
                Message.Error(Program.GeneralForm, "Не вдалось прочитати!");
                return;
            }

            NotebookFunction.CreateNotebookPage(Program.GeneralNotebook, page.Caption, () => page);
            await NotebookFunction.AddLockObjectFunc(Program.GeneralNotebook, page.Name, page.Елемент);
            await page.LockInfo(page.Елемент);
            page.SetValue();
        }

        public static async ValueTask SetDeletionLabel(UnigueID unigueID)
        {
            ЧекККМ_Pointer Вказівник = new(unigueID);
            bool? label = await Вказівник.GetDeletionLabel();
            if (label.HasValue) await Вказівник.SetDeletionLabel(!label.Value);
        }

        public static async ValueTask<UnigueID?> Copy(UnigueID unigueID)
        {
            ЧекККМ_Objest Обєкт = new ЧекККМ_Objest();
            if (await Обєкт.Read(unigueID))
            {
                ЧекККМ_Objest Новий = await Обєкт.Copy(true);
                await Новий.Save();

                    await Новий.Товари_TablePart.Save(false); // Таблична частина "Товари"

                return Новий.UnigueID;
            }
            else
            {
                Message.Error(Program.GeneralForm, "Не вдалось прочитати!");
                return null;
            }
        }
    }
}

/*
        ЧекККМ_Елемент.cs
        Елемент
*/

using Gtk;
using InterfaceGtk;
using AccountingSoftware;

using GeneratedCode;
using GeneratedCode.Константи;
using GeneratedCode.Довідники;
using GeneratedCode.Документи;
using GeneratedCode.Перелічення;

namespace StorageAndTrade
{
    class ЧекККМ_Елемент : ДокументЕлемент
    {
        public ЧекККМ_Objest Елемент { get; init; } = new ЧекККМ_Objest();

        #region Fields
        Entry НомерДок = new Entry() { WidthRequest = 500 };
                        DateTimeControl ДатаДок = new DateTimeControl();
                Entry Коментар = new Entry() { WidthRequest = 500 };
                        CompositePointerControl Основа = new CompositePointerControl() { BoundConfType = "Документи.ЧекККМ.Основа" };
                Організації_PointerControl Організація = new Організації_PointerControl() { Caption = "Організація", WidthPresentation = 500 };
                Валюти_PointerControl Валюта = new Валюти_PointerControl() { Caption = "Валюта", WidthPresentation = 500 };
                NumericControl СумаДокументу = new NumericControl();
                Склади_PointerControl Склад = new Склади_PointerControl() { Caption = "Склад", WidthPresentation = 500 };
                Користувачі_PointerControl Автор = new Користувачі_PointerControl() { Caption = "Автор", WidthPresentation = 500 };
                КасиККМ_PointerControl КасаККМ = new КасиККМ_PointerControl() { Caption = "КасаККМ", WidthPresentation = 500 };

        #endregion

        #region TabularParts

            // Таблична частина "Товари" 
            ЧекККМ_ТабличнаЧастина_Товари Товари = new ЧекККМ_ТабличнаЧастина_Товари() { WidthRequest = 500, HeightRequest = 300 };

        #endregion

        public ЧекККМ_Елемент()
        {
            Елемент.UnigueIDChanged += UnigueIDChanged;
            Елемент.CaptionChanged += CaptionChanged;

            CreateDocName(ЧекККМ_Const.FULLNAME, НомерДок, ДатаДок);

            CreateField(HBoxComment, "Коментар:", Коментар);

                // Таблична частина "Товари" 
                NotebookTablePart.InsertPage(Товари, new Label("Товари"), 0);

                NotebookTablePart.CurrentPage = 0;

        }

        protected override void CreateContainer1(Box vBox)
        {

        }

        protected override void CreateContainer2(Box vBox)
        {

        }

        protected override void CreateContainer3(Box vBox)
        {

                // Основа
                CreateField(vBox, null, Основа);

                // Організація
                CreateField(vBox, null, Організація);

                // Валюта
                CreateField(vBox, null, Валюта);

                // СумаДокументу
                CreateField(vBox, "СумаДокументу:", СумаДокументу);

                // Склад
                CreateField(vBox, null, Склад);

                // Автор
                CreateField(vBox, null, Автор);

                // КасаККМ
                CreateField(vBox, null, КасаККМ);

        }

        protected override void CreateContainer4(Box vBox)
        {

        }

        #region Присвоєння / зчитування значень

        public override async void SetValue()
        {
            НомерДок.Text = Елемент.НомерДок;
                            ДатаДок.Value = Елемент.ДатаДок;
                    Коментар.Text = Елемент.Коментар;
                            Основа.Pointer = Елемент.Основа;
                    Організація.Pointer = Елемент.Організація;
                    Валюта.Pointer = Елемент.Валюта;
                    СумаДокументу.Value = Елемент.СумаДокументу;
                    Склад.Pointer = Елемент.Склад;
                    Автор.Pointer = Елемент.Автор;
                    КасаККМ.Pointer = Елемент.КасаККМ;

                // Таблична частина "Товари" 
                Товари.ЕлементВласник = Елемент; 
                await Товари.LoadRecords();

        }

        protected override void GetValue()
        {
            Елемент.НомерДок = НомерДок.Text;
                            Елемент.ДатаДок = ДатаДок.Value;
                    Елемент.Коментар = Коментар.Text;
                            Елемент.Основа = Основа.Pointer;
                    Елемент.Організація = Організація.Pointer;
                    Елемент.Валюта = Валюта.Pointer;
                    Елемент.СумаДокументу = СумаДокументу.Value;
                    Елемент.Склад = Склад.Pointer;
                    Елемент.Автор = Автор.Pointer;
                    Елемент.КасаККМ = КасаККМ.Pointer;

        }


        /*string КлючовіСловаДляПошуку()
        {
            return "\n {Організація.Назва} {Валюта.Назва} {Склад.Назва} {Автор.Назва} {КасаККМ.Назва}"
             + Товари.КлючовіСловаДляПошуку();
        }*/


        #endregion

        protected override async ValueTask<bool> Save()
        {
            bool isSaved = false;
            try
            {
                if(await Елемент.Save())
                {
                    await Товари.SaveRecords(); // Таблична частина "Товари"

                    isSaved = true;
                }
            }
            catch (Exception ex)
            {
                ФункціїДляПовідомлень.ДодатиПовідомлення(Елемент.GetBasis(), Caption, ex);
            }
            return isSaved;
        }

        protected override async ValueTask<bool> SpendTheDocument(bool spendDoc)
        {
            if (spendDoc)
            {
                bool isSpend = await Елемент.SpendTheDocument(Елемент.ДатаДок);
                if (!isSpend) ФункціїДляПовідомлень.ПоказатиПовідомлення(Елемент.UnigueID);
                return isSpend;
            }
            else
            {
                await Елемент.ClearSpendTheDocument();
                return true;
            }
        }

        protected override void ReportSpendTheDocument(UnigueID unigueID)
        {
            СпільніФорми_РухДокументуПоРегістрах.СформуватиЗвіт(new ЧекККМ_Pointer(unigueID));
        }

        protected override async ValueTask InJournal(UnigueID unigueID)
        {
            ЧекККМ page = new ЧекККМ() { SelectPointerItem = unigueID };
            NotebookFunction.CreateNotebookPage(Program.GeneralNotebook, ЧекККМ_Const.FULLNAME, () => page);
            await page.SetValue();
        }
    }
}

/*     
        ЧекККМ.cs
        Список
*/

using Gtk;
using InterfaceGtk;
using AccountingSoftware;

using GeneratedCode;
using ТабличніСписки = GeneratedCode.Документи.ТабличніСписки;
using GeneratedCode.Документи;

namespace StorageAndTrade
{
    public class ЧекККМ : ДокументЖурнал
    {
        public ЧекККМ() : base()
        {
            ТабличніСписки.ЧекККМ_Записи.AddColumns(TreeViewGrid);
        }

        #region Override

        public override async ValueTask LoadRecords()
        {
            ТабличніСписки.ЧекККМ_Записи.ДодатиВідбірПоПеріоду(TreeViewGrid, Період.Period, Період.DateStart, Період.DateStop);

            await ТабличніСписки.ЧекККМ_Записи.LoadRecords(TreeViewGrid, SelectPointerItem, DocumentPointerItem);
        }

        public override async ValueTask LoadRecords_OnSearch(string searchText)
        {
            ТабличніСписки.ЧекККМ_Записи.ОчиститиВідбір(TreeViewGrid);

            //Відбори
            ТабличніСписки.ЧекККМ_Записи.ДодатиВідбір(TreeViewGrid, ЧекККМ_Функції.Відбори(searchText));

            await ТабличніСписки.ЧекККМ_Записи.LoadRecords(TreeViewGrid);
        }

        public async override ValueTask LoadRecords_OnFilter()
        {
            await ТабличніСписки.ЧекККМ_Записи.LoadRecords(TreeViewGrid);
        }

        public async ValueTask UpdateRecords(List<ObjectChanged> recordsChanged)
        {
            await ТабличніСписки.ЧекККМ_Записи.UpdateRecords(TreeViewGrid, recordsChanged);
        }

        protected override void FillFilterList(ListFilterControl filterControl)
        {
            ТабличніСписки.ЧекККМ_Записи.CreateFilter(TreeViewGrid, filterControl);
        }

        protected override async ValueTask OpenPageElement(bool IsNew, UnigueID? unigueID = null)
        {
            await ЧекККМ_Функції.OpenPageElement(IsNew, unigueID, CallBack_LoadRecords);
        }

        protected override async ValueTask SetDeletionLabel(UnigueID unigueID)
        {
            await ЧекККМ_Функції.SetDeletionLabel(unigueID);
        }

        protected override async ValueTask<UnigueID?> Copy(UnigueID unigueID)
        {
            return await ЧекККМ_Функції.Copy(unigueID);
        }

        protected override async ValueTask VersionsHistory(UnigueID unigueID)
        {
            await СпільніФорми_ІсторіяЗміниДаних_Список.Сформувати(new ЧекККМ_Pointer(unigueID).GetBasis());
        }

        const string КлючНалаштуванняКористувача = "Документи.ЧекККМ";

        protected override async ValueTask BeforeSetValue()
        {
            await ФункціїНалаштуванняКористувача.ОтриматиПеріодДляЖурналу(КлючНалаштуванняКористувача + KeyForSetting, Період);
            NotebookFunction.AddChangeFunc(Program.GeneralNotebook, Name, UpdateRecords, ЧекККМ_Const.POINTER);
        }

        protected override async void PeriodChanged()
        {
            ФункціїНалаштуванняКористувача.ЗаписатиПеріодДляЖурналу(КлючНалаштуванняКористувача + KeyForSetting, Період.Period.ToString(), Період.DateStart, Період.DateStop);
            await BeforeLoadRecords();
        }

        protected override async ValueTask SpendTheDocument(UnigueID unigueID, bool spendDoc)
        {
            ЧекККМ_Objest? Обєкт = await new ЧекККМ_Pointer(unigueID).GetDocumentObject(true);
            if (Обєкт == null) return;

            if (spendDoc)
            {
                if (!await Обєкт.SpendTheDocument(Обєкт.ДатаДок))
                    ФункціїДляПовідомлень.ПоказатиПовідомлення(Обєкт.UnigueID);
            }
            else
                await Обєкт.ClearSpendTheDocument();
        }

        protected override void ReportSpendTheDocument(UnigueID unigueID)
        {
            СпільніФорми_РухДокументуПоРегістрах.СформуватиЗвіт(new ЧекККМ_Pointer(unigueID));
        }

        protected override bool IsExportXML() { return false; } //Дозволити експорт документу
        protected override async ValueTask ExportXML(UnigueID unigueID, string pathToFolder)
        {
            ЧекККМ_Pointer Вказівник = new ЧекККМ_Pointer(unigueID);
            await Вказівник.GetPresentation();
            string path = System.IO.Path.Combine(pathToFolder, $"{Вказівник.Назва}.xml");

            await ЧекККМ_Export.ToXmlFile(Вказівник, path);
            ФункціїДляПовідомлень.ДодатиІнформаційнеПовідомлення(Вказівник.GetBasis(), Вказівник.Назва, $"Вигружено у файл: {path}");
        }

        /*
        protected override async ValueTask PrintingDoc(UnigueID unigueID)
        {
            await ЧекККМ_Друк.PDF(unigueID);
        }
        */

        #endregion
    }
}

/*     
        ЧекККМ_PointerControl.cs
        PointerControl
*/
using Gtk;
using InterfaceGtk;
using AccountingSoftware;
using GeneratedCode.Документи;

namespace StorageAndTrade
{
    public class ЧекККМ_PointerControl : PointerControl
    {
        event EventHandler<ЧекККМ_Pointer> PointerChanged;

        public ЧекККМ_PointerControl()
        {
            pointer = new ЧекККМ_Pointer();
            WidthPresentation = 300;
            Caption = $"{ЧекККМ_Const.FULLNAME}:";
            PointerChanged += async (_, pointer) => Presentation = pointer != null ? await pointer.GetPresentation() : "";
        }

        ЧекККМ_Pointer pointer;
        public ЧекККМ_Pointer Pointer
        {
            get
            {
                return pointer;
            }
            set
            {
                pointer = value;
                PointerChanged?.Invoke(null, pointer);
            }
        }

        protected override void OpenSelect(object? sender, EventArgs args)
        {
            BeforeClickOpenFunc?.Invoke();
            ЧекККМ page = new ЧекККМ
            {
                DocumentPointerItem = Pointer.UnigueID,
                CallBack_OnSelectPointer = selectPointer =>
                {
                    Pointer = new ЧекККМ_Pointer(selectPointer);
                    AfterSelectFunc?.Invoke();
                }
            };

            NotebookFunction.CreateNotebookPage(Program.GeneralNotebook, $"Вибір - {ЧекККМ_Const.FULLNAME}", () => page);
            page.SetValue();
        }

        protected override void OnClear(object? sender, EventArgs args)
        {
            Pointer = new ЧекККМ_Pointer();
            AfterSelectFunc?.Invoke();
            AfterClearFunc?.Invoke();
        }
    }
}

QueryBlock.Query:
INSERT INTO {ТовариНаСкладах_Залишки_TablePart.TABLE} ( uid, {ТовариНаСкладах_Залишки_TablePart.Період}, {ТовариНаСкладах_Залишки_TablePart.Номенклатура}, {ТовариНаСкладах_Залишки_TablePart.ХарактеристикаНоменклатури}, {ТовариНаСкладах_Залишки_TablePart.Склад}, {ТовариНаСкладах_Залишки_TablePart.Серія}, {ТовариНаСкладах_Залишки_TablePart.ВНаявності}, {ТовариНаСкладах_Залишки_TablePart.ДоВідвантаження} ) SELECT uuid_generate_v4(), date_trunc('day', ТовариНаСкладах.period::timestamp) AS Період, ТовариНаСкладах.{ТовариНаСкладах_Const.Номенклатура} AS Номенклатура, ТовариНаСкладах.{ТовариНаСкладах_Const.ХарактеристикаНоменклатури} AS ХарактеристикаНоменклатури, ТовариНаСкладах.{ТовариНаСкладах_Const.Склад} AS Склад, ТовариНаСкладах.{ТовариНаСкладах_Const.Серія} AS Серія,
/* ВНаявності */ SUM(CASE WHEN ТовариНаСкладах.income = true THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ВНаявності} ELSE -ТовариНаСкладах.{ТовариНаСкладах_Const.ВНаявності} END) AS ВНаявності,
/* ДоВідвантаження */ SUM(CASE WHEN ТовариНаСкладах.income = true THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ДоВідвантаження} ELSE -ТовариНаСкладах.{ТовариНаСкладах_Const.ДоВідвантаження} END) AS ДоВідвантаження
FROM {ТовариНаСкладах_Const.TABLE} AS ТовариНаСкладах
WHERE date_trunc('day', ТовариНаСкладах.period::timestamp) = @ПеріодДеньВідбір
GROUP BY Період, Номенклатура, ХарактеристикаНоменклатури, Склад, Серія
HAVING
/* ВНаявності */ SUM(CASE WHEN ТовариНаСкладах.income = true THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ВНаявності} ELSE -ТовариНаСкладах.{ТовариНаСкладах_Const.ВНаявності} END) != 0 OR
/* ДоВідвантаження */ SUM(CASE WHEN ТовариНаСкладах.income = true THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ДоВідвантаження} ELSE -ТовариНаСкладах.{ТовариНаСкладах_Const.ДоВідвантаження} END) != 0

INSERT INTO {ТовариНаСкладах_ЗалишкиТаОбороти_TablePart.TABLE} ( uid, {ТовариНаСкладах_ЗалишкиТаОбороти_TablePart.Період}, {ТовариНаСкладах_ЗалишкиТаОбороти_TablePart.Номенклатура}, {ТовариНаСкладах_ЗалишкиТаОбороти_TablePart.ХарактеристикаНоменклатури}, {ТовариНаСкладах_ЗалишкиТаОбороти_TablePart.Склад}, {ТовариНаСкладах_ЗалишкиТаОбороти_TablePart.Серія}, {ТовариНаСкладах_ЗалишкиТаОбороти_TablePart.ВНаявностіПрихід}, {ТовариНаСкладах_ЗалишкиТаОбороти_TablePart.ВНаявностіРозхід}, {ТовариНаСкладах_ЗалишкиТаОбороти_TablePart.ВНаявностіЗалишок}, {ТовариНаСкладах_ЗалишкиТаОбороти_TablePart.ДоВідвантаженняПрихід}, {ТовариНаСкладах_ЗалишкиТаОбороти_TablePart.ДоВідвантаженняРозхід}, {ТовариНаСкладах_ЗалишкиТаОбороти_TablePart.ДоВідвантаженняЗалишок} ) SELECT uuid_generate_v4(), date_trunc('day', ТовариНаСкладах.period::timestamp) AS Період, ТовариНаСкладах.{ТовариНаСкладах_Const.Номенклатура} AS Номенклатура, ТовариНаСкладах.{ТовариНаСкладах_Const.ХарактеристикаНоменклатури} AS ХарактеристикаНоменклатури, ТовариНаСкладах.{ТовариНаСкладах_Const.Склад} AS Склад, ТовариНаСкладах.{ТовариНаСкладах_Const.Серія} AS Серія,
/* ВНаявності */ SUM(CASE WHEN ТовариНаСкладах.income = true THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ВНаявності} ELSE 0 END) AS ВНаявностіПрихід, SUM(CASE WHEN ТовариНаСкладах.income = false THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ВНаявності} ELSE 0 END) AS ВНаявностіРозхід, SUM(CASE WHEN ТовариНаСкладах.income = true THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ВНаявності} ELSE -ТовариНаСкладах.{ТовариНаСкладах_Const.ВНаявності} END) AS ВНаявностіЗалишок,
/* ДоВідвантаження */ SUM(CASE WHEN ТовариНаСкладах.income = true THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ДоВідвантаження} ELSE 0 END) AS ДоВідвантаженняПрихід, SUM(CASE WHEN ТовариНаСкладах.income = false THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ДоВідвантаження} ELSE 0 END) AS ДоВідвантаженняРозхід, SUM(CASE WHEN ТовариНаСкладах.income = true THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ДоВідвантаження} ELSE -ТовариНаСкладах.{ТовариНаСкладах_Const.ДоВідвантаження} END) AS ДоВідвантаженняЗалишок
FROM {ТовариНаСкладах_Const.TABLE} AS ТовариНаСкладах
WHERE date_trunc('day', ТовариНаСкладах.period::timestamp) = @ПеріодДеньВідбір
GROUP BY Період, Номенклатура, ХарактеристикаНоменклатури, Склад, Серія
HAVING
/* ВНаявності */ SUM(CASE WHEN ТовариНаСкладах.income = true THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ВНаявності} ELSE 0 END) != 0 OR SUM(CASE WHEN ТовариНаСкладах.income = false THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ВНаявності} ELSE 0 END) != 0 OR SUM(CASE WHEN ТовариНаСкладах.income = true THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ВНаявності} ELSE -ТовариНаСкладах.{ТовариНаСкладах_Const.ВНаявності} END) != 0 OR
/* ДоВідвантаження */ SUM(CASE WHEN ТовариНаСкладах.income = true THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ДоВідвантаження} ELSE 0 END) != 0 OR SUM(CASE WHEN ТовариНаСкладах.income = false THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ДоВідвантаження} ELSE 0 END) != 0 OR SUM(CASE WHEN ТовариНаСкладах.income = true THEN ТовариНаСкладах.{ТовариНаСкладах_Const.ДоВідвантаження} ELSE -ТовариНаСкладах.{ТовариНаСкладах_Const.ДоВідвантаження} END) != 0

